INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251471, 'Etravirine', 'Paritaprevir', 'Major', 'Coadministration with moderate to potent inducers of CYP450 isoenzymes may significantly decrease the plasma concentrations of ombitasvir, paritaprevir, and dasabuvir.  The proposed mechanism may involve induction of CYP450 3A4.', NULL, 'Concomitant use of CYP450 3A4 inducers such etravirine or nevirapine with ombitasvir/paritaprevir/ritonavir with or without dasabuvir is considered contraindicated by some authorities.', NULL, 'Concomitant use of CYP450 3A4 inducers such etravirine or nevirapine with ombitasvir/paritaprevir/ritonavir with or without dasabuvir is considered contraindicated by some authorities.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297870/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Viekira Pak (dasabuvir/ombitasvir/paritaprev/ritonav)." AbbVie US LLC  (2022):[4] Cerner Multum, Inc. "Canadian Product Information." O 0 (2015):[5] Department of Health and
