INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251488, 'Glasdegib', 'Paritaprevir', 'Moderate', 'Coadministration with glasdegib may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) efflux transporters.  In vitro data suggest that glasdegib may have the potential to inhibit P-gp (in the gastrointestinal (GI) tract) and BCRP (systemically and in the GI tract) at clinically relevant concentrations, which may result in decreased clearance of concomitantly administered P-gp and BCRP substrates.', NULL, 'Caution is advised if glasdegib is coadministered with drugs that are substrates of P-gp and/or BCRP, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if glasdegib is coadministered with drugs that are substrates of P-gp and/or BCRP, particularly those with a narrow therapeutic range.  Clinical and laboratory monitoring should be considered whenever glasdegib is added to or withdrawn from therapy with these drugs, and dosages adjusted as necessary.  Patients should be monitored for the development of adverse effects.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297895/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Daurismo (glasdegib)." Pfizer U.S. Pharmaceuticals Group  (2018):', 1767363028);
