    ) VALUES (1474478, 'Terfenadine', 'Paroxetine', 'Minor', 'Based on both in vitro and in vivo studies, paroxetine given at normally recommended dosages does not appear to significantly affect the pharmacokinetics or cardiovascular effects of terfenadine. Caution may be warranted when high dosages of paroxetine is administered.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
