        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1474569, 'Dotatate', 'Pasireotide', 'Moderate', 'INTERVAL: Non-radioactive somatostatin analogs may interfere with the uptake of Ga-68 dotatate into neuroendocrine tumors by competitively binding to somatostatin receptors in tumor tissues. This may affect diagnostic imaging results.', NULL, 'The manufacturer recommends imaging patients with gallium Ga-68 dotatate positron emission tomography (PET) just prior to dosing with long-acting somatostatin analogs.', NULL, 'The manufacturer recommends imaging patients with gallium Ga-68 dotatate positron emission tomography (PET) just prior to dosing with long-acting somatostatin analogs. Short-acting formulations of somatostatin analogs can be used up to 24 hours before imaging with gallium Ga-68 dotatate.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
