INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251522, 'Irinotecan', 'Dasabuvir', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or UGT1A1 may increase the plasma concentrations of irinotecan and its active metabolite, SN-38.  CYP450 3A4 and UGT1A1 are the isoenzymes responsible for the metabolic conversion of irinotecan to its inactive metabolite, APC.', NULL, 'Caution is advised when irinotecan is prescribed with CYP450 3A4 or UGT1A1 inhibitors.', NULL, 'Caution is advised when irinotecan is prescribed with CYP450 3A4 or UGT1A1 inhibitors.  Patients should be monitored for toxicities such as diarrhea, myelosuppression, thromboembolism, and interstitial lung disease, and the irinotecan dosage adjusted accordingly or treatment discontinued as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297944/', '[1] "Product Information. Camptosar (irinotecan)." Pharmacia and Upjohn  (2001):[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Canadian Pharmacists Association "e-CPS.  http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink"   (2006):[4] Corona G, Vaccher E, Sandron S, et al. "Lopinavir-ritonavir dramatically affects the pharmacokinetics of irinotecan in HIV patients with Kaposi''s sarcoma." Clin Pharmacol Ther 83 (2008):  601-6[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] Phansalker S, Desai AA, Bell D, et al. "High-priority drug-drug interactions for use in electronic health records." J Am Med Inform Assoc 19 (2012):  735-43[7] "Product Information. Onivyde (irinotecan liposomal)." Merrimack Pharmaceuticals  (2015):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251523, 'Isavuconazonium', 'Paritaprevir', 'Moderate', 'Coadministration with isavuconazonium sulfate (prodrug of isavuconazole) may increase the plasma concentrations of drugs that are substrates of P-glycoprotein (P-gp), such as dabigatran.  The proposed mechanism is isavuconazole inhibition of P-gp-mediated efflux of these drugs.', NULL, 'Caution is advised if isavuconazonium sulfate is used concomitantly with drugs that are substrates of P-gp, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if isavuconazonium sulfate is used concomitantly with drugs that are substrates of P-gp, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever isavuconazonium sulfate is added to or withdrawn from therapy with these drugs.  Patients receiving concomitant dabigatran should be monitored closely for the development of bleeding complications and the dosage of dabigatran adjusted as necessary.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297945/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363028);
