INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251581, 'Pravastatin', 'Paritaprevir', 'Major', 'Coadministration with ombitasvir/paritaprevir/ritonavir plus dasabuvir may significantly increase the plasma concentrations of pravastatin.  The mechanism may involve inhibition of OATP1B1-mediated hepatic uptake of pravastatin by both paritaprevir and ritonavir.', NULL, 'The benefits of using pravastatin with ombitasvir/paritaprevir/ritonavir plus dasabuvir should be carefully weighed against the potentially increased risk of myopathy including rhabdomyolysis.', NULL, 'The benefits of using pravastatin with ombitasvir/paritaprevir/ritonavir plus dasabuvir should be carefully weighed against the potentially increased risk of myopathy including rhabdomyolysis.  If coadministration is required, the lowest effective dosage of pravastatin should be used and close monitoring for musculoskeletal toxicity is recommended.  The dosage of pravastatin should be limited to 40 mg/day.  Alternatively, an interruption of pravastatin may be considered during treatment with ombitasvir/paritaprevir/ritonavir plus dasabuvir, or a different HMG-CoA reductase inhibitor such as fluvastatin or pitavastatin may be substituted.  All patients receiving statin therapy should be advised to promptly report any unexplained muscle pain, tenderness or weakness, particularly if accompanied by fever, malaise and/or dark colored urine.  Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism, Distribution', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298039/', '[1] "Product Information. Viekira Pak (dasabuvir/ombitasvir/paritaprev/ritonav)." AbbVie US LLC  (2022):', 1767363028);
