INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251585, 'Quinestrol', 'Paritaprevir', 'Major', 'Theoretical concerns exist that coadministration of estrogen-containing products may increase the risk of liver enzyme elevations associated with the use of ombitasvir/paritaprevir/ritonavir, with or without dasabuvir.  The proposed mechanism is the inhibition of UDP-glucuronosyltransferase (UGT) by ombitasvir, paritaprevir, and dasabuvir.', NULL, 'Caution is recommended if estrogen-containing products are used in patients receiving ombitasvir/paritaprevir/ritonavir, with or without dasabuvir.', NULL, 'Caution is recommended if estrogen-containing products are used in patients receiving ombitasvir/paritaprevir/ritonavir, with or without dasabuvir.  Liver function tests should be performed on all patients during the first 4 weeks of treatment and as clinically indicated thereafter.  If ALT is elevated above baseline at any time during treatment, the test should be repeated and monitored closely.  Therapy should be discontinued if ALT levels remain persistently greater than 10 times the ULN, or if ALT elevations are accompanied by signs or symptoms of liver inflammation or increasing conjugated bilirubin, alkaline phosphatase, or INR.  Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298044/', '[1] "Product Information. Viekira Pak (dasabuvir/ombitasvir/paritaprev/ritonav)." AbbVie US LLC  (2022):', 1767363028);
