INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251601, 'Dasabuvir', 'Sacituzumab govitecan', 'Major', 'Coadministration of sacituzumab govitecan with inhibitors of uridine diphosphate glucuronosyltransferase 1A1 (UGT1A1) may increase the risk of adverse effects, such as nausea, vomiting, diarrhea, and neutropenia.  The proposed mechanism involves increased exposure to the pharmacologically active metabolite SN-38, a topoisomerase I inhibitor linked to sacituzumab govitecan, which is metabolized via UGT1A1.  Genetic variants of the UGT1A1 gene such as the UGT1A1*28 allele lead to reduced UGT1A1 enzyme activity and patients who are homozygous for the UGT1A1*28 allele are at increased risk for neutropenia from SN-38.', NULL, 'Concomitant use of sacituzumab govitecan with UGT1A1 inhibitors should be avoided.', NULL, 'Concomitant use of sacituzumab govitecan with UGT1A1 inhibitors should be avoided.  If coadministration is unavoidable, patients should be closely monitored for the development of severe nausea, vomiting, diarrhea, and neutropenia.  Patients should be advised to promptly report symptoms such as chills, fever, malaise, sore throat, mouth sores, unusual fatigue, bruising, or bleeding.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298064/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Trodelvy (sacituzumab govitecan)." Immunomedics  (2020):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251602, 'Dasabuvir', 'Safinamide', 'Moderate', 'Coadministration with safinamide may increase the plasma concentrations of drugs that are substrates of the breast cancer resistance protein (BCRP) transporter.  The proposed mechanism is decreased clearance due to intestinal BCRP-inhibition by safinamide.', NULL, 'Caution is advised when safinamide is prescribed with drugs that are BCRP substrates, particularly those with a narrow therapeutic range such as methotrexate, topotecan, and irinotecan.', NULL, 'Caution is advised when safinamide is prescribed with drugs that are BCRP substrates, particularly those with a narrow therapeutic range such as methotrexate, topotecan, and irinotecan.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever safinamide is added to or withdrawn from therapy.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298065/', '[1] "Product Information. Xadago (safinamide)." US WorldMeds LLC  (2017):', 1767363028);
