INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251619, 'Tacrolimus', 'Paritaprevir', 'Major', 'Coadministration of ombitasvir/paritaprevir/ritonavir, with or without dasabuvir, may significantly increase the blood concentrations of tacrolimus, sirolimus, and everolimus.  The proposed mechanism involves ritonavir inhibition of intestinal and hepatic CYP450 3A4, the isoenzyme responsible for the metabolic clearance of these immunosuppressants.  Enhanced oral bioavailability due to inhibition of intestinal P-glycoprotein (P-gp) efflux transporter by ritonavir, paritaprevir, and dasabuvir may also contribute.', NULL, 'Concomitant use of tacrolimus, sirolimus, or everolimus in combination with ombitasvir/paritaprevir/ritonavir, with or without dasabuvir, is considered contraindicated.', NULL, 'Concomitant use of tacrolimus, sirolimus, or everolimus in combination with ombitasvir/paritaprevir/ritonavir, with or without dasabuvir, is considered contraindicated.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298093/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Viekira Pak (dasabuvir/ombitasvir/paritaprev/ritonav)." AbbVie US LLC  (2022):[3] "Product Information. Technivie (ombitasvir/paritaprevir/ritonavir)." AbbVie US LLC  (2015):[4] Badri P, Dutta S, Coakley E, et al. "Pharmacokinetics and dose recommendations for cyclosporine and tacrolimus when coadministered with ABT-450, ombitasvir, and dasabuvir." Am J Transplant 15 (2015):  1313-22', 1767363028);
