INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251645, 'Valsartan', 'Paritaprevir', 'Moderate', 'Coadministration with inhibitors of the hepatic uptake transporter OATP 1B1 (e.g., rifampin, cyclosporine, paritaprevir) or the hepatic efflux transporter MRP2 (e.g., ritonavir) may increase the systemic exposure to valsartan, which is a substrate of both transporters.', NULL, 'Caution is advised if valsartan is used in combination with inhibitors of OATP 1B1 or MRP2.', NULL, 'Caution is advised if valsartan is used in combination with inhibitors of OATP 1B1 or MRP2.  Pharmacologic response and blood pressure should be monitored more closely following the addition, discontinuation, or change of dosage of the transporter inhibitor, and the valsartan dosage adjusted as necessary.', 'Distribution', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298134/', '[1] "Product Information. Diovan (valsartan)." Novartis Pharmaceuticals  (2001):[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Exforge (amlodipine-valsartan)." Novartis Pharmaceuticals  (2007):[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] "Product Information. Viekira Pak (dasabuvir/ombitasvir/paritaprev/ritonav)." AbbVie US LLC  (2022):[6] "Product Information. Nexlizet (bempedoic acid-ezetimibe)." Esperion Therapeutics  (2020):[7] "Product Information. Nexletol (bempedoic acid)." Esperion Therapeutics  (2020):', 1767363028);
