INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251658, 'Doravirine', 'Telotristat ethyl', 'Major', 'Coadministration with moderate inducers of CYP450 3A4 may significantly decrease the plasma concentrations of doravirine, which is primarily metabolized by the isoenzyme.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, concomitant use of doravirine with moderate CYP450 3A4 inducers should generally be avoided.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, concomitant use of doravirine with moderate CYP450 3A4 inducers should generally be avoided.  If coadministration is necessary, some authorities recommend that the doravirine dose be increased to 100 mg twice daily (approximately 12 hours apart) (UK).', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298181/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] EMA. European Medicines Agency. European Union "EMA - List of medicines under additional monitoring.  http://www.ema.europa.eu/ema/index.jsp?curl=pages/regulation/document_listing/document_listing_000366.jsp&mid=WC0b01ac058067c852"   (2013):[3] "Product Information. Pifeltro (doravirine)." Merck & Co., Inc  (2018):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251659, 'Brentuximab vedotin', 'Quinidine', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of monomethyl auristatin E (MMAE), the microtubule-disrupting component of brentuximab vedotin.  In vitro, MMAE was found to be a substrate of the P-gp efflux transporter.', NULL, 'Caution is advised when brentuximab is used with P-gp inhibitors.', NULL, 'Caution is advised when brentuximab is used with P-gp inhibitors.  Close monitoring for adverse effects including neutropenia, infection, peripheral neuropathy, and hepatotoxicity is recommended.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298375/', '[1] "Product Information. Adcetris (brentuximab vedotin)." Seattle Genetics Inc  (2011):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251660, 'Glycopyrronium (topical)', 'Quinidine', 'Moderate', 'The potential exists for additive anticholinergic effects such as mydriasis, blurred vision, heat intolerance, fever, dry mouth, tachycardia, urinary retention, constipation, and glaucoma (onset or exacerbation) when topical or inhaled anticholinergic agents are used with each other or with other agents that possess anticholinergic properties.  The risk of systemic anticholinergic effects following topical administration depends on variables such as strength of the product, size of the application area, frequency of application, and use of occlusive dressing.  Systemic effects are uncommon following oral inhalation or nasal administration due to the poor absorption of quaternary ammonium compounds from gastrointestinal and nasal mucosa.', NULL, 'Topical and inhaled anticholinergic preparations should preferably not be used in combination with other anticholinergic agents or agents with significant anticholinergic effects such as antihistamines, antispasmodics, neuroleptics, phenothiazines, skeletal muscle relaxants, tricyclic antidepressants, and class IA antiarrhythmics (especially disopyramide).', NULL, 'Topical and inhaled anticholinergic preparations should preferably not be used in combination with other anticholinergic agents or agents with significant anticholinergic effects such as antihistamines, antispasmodics, neuroleptics, phenothiazines, skeletal muscle relaxants, tricyclic antidepressants, and class IA antiarrhythmics (especially disopyramide).  Caution is advised if concomitant use cannot be avoided, particularly in the elderly and those with significantly impaired renal and/or hepatic function.  Measures should be taken whenever possible to minimize ocular exposure to these drugs, such as keeping eyes closed during oral inhalation, use of a mouthpiece rather than face mask during nebulization, and not touching the eyes following topical application until hands are washed with soap and water.  Patients should be advised to contact their physician if they experience excessive anticholinergic adverse effects or signs and symptoms of angle-closure glaucoma (e.g., eye pain or discomfort; blurred vision; visual halos; colored images in association with red eyes from conjunctival congestion or corneal edema).', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298376/', '[1] "Product Information. Atrovent (ipratropium)." Boehringer-Ingelheim  (2002):[2] "Product Information. Combivent (albuterol-ipratropium)." Boehringer-Ingelheim  (2001):[3] "Product Information. Spiriva (tiotropium)." Boehringer Ingelheim  (2002):[4] "Product Information. Tudorza Pressair (aclidinium)." Forest Pharmaceuticals  (2012):[5] Cole JM, Sheehan AH, Jordan JK "Concomitant use of ipratropium and tiotropium in chronic obstructive plmonary disease." Ann Pharmacother 46 (2012):  1717-21[6] "Product Information. Anoro Ellipta (umeclidinium-vilanterol)." GlaxoSmithKline  (2014):[7] "Product Information. Qbrexza (glycopyrrolate topical)." Dermira, Inc.  (2018):[8] "Product Information. Yupelri (revefenacin)." Mylan Specialty  (2018):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251661, 'Quinidine', 'Patiromer', 'Moderate', 'Patiromer can bind to some orally administered drugs, which may decrease their gastrointestinal absorption and reduce their effectiveness.  According to the manufacturer, in vitro studies observed potentially clinically significant binding between patiromer and telmisartan, bisoprolol, carvedilol, nebivolol, mycophenolate mofetil, quinidine, and thiamine.', NULL, 'Patiromer should be administered at least 3 hours before or 3 hours after other oral medications, particularly ciprofloxacin, levothyroxine, metformin, telmisartan, bisoprolol, carvedilol, nebivolol, mycophenolate mofetil, quinidine, and thiamine.', NULL, 'Patiromer should be administered at least 3 hours before or 3 hours after other oral medications, particularly ciprofloxacin, levothyroxine, metformin, telmisartan, bisoprolol, carvedilol, nebivolol, mycophenolate mofetil, quinidine, and thiamine.  Alternatives to patiromer or the other medications should be considered if adequate dosing separation is not possible.  Otherwise, clinical response and/or blood levels should be monitored where possible.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298378/', '[1] "Product Information. Veltassa (patiromer)." Relypsa, Inc.  (2015):[2] "Product Information. Veltassa (patiromer)." Vifor Pharma Pty Ltd  (2023):[3] "Product Information. Veltassa (patiromer)." Vifor Fresenius Medical Care Renal Pharma UK Ltd  (2022):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251662, 'Somapacitan', 'Quinidine', 'Moderate', 'Coadministration with human growth hormone may decrease the plasma concentrations of drugs that are metabolized by CYP450 3A4.  Data from an interaction study performed in growth hormone deficient adults suggest that growth hormone (somatropin) administration can increase the clearance of compounds known to be metabolized by cytochrome P450 isoenzymes, especially CYP450 3A4.', NULL, 'Caution is advised when human growth hormone is used concurrently with drugs that are known CYP450 3A4 substrates, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when human growth hormone is used concurrently with drugs that are known CYP450 3A4 substrates, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever human growth hormone is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298379/', '[1] "Product Information. Humatrope (somatropin)." Lilly, Eli and Company  (2003):[2] "Product Information. Zorbtive (somatropin)." Serono Laboratories Inc  (2004):[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Cerner Multum, Inc. "Australian Product Information." O 0', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251663, 'Telotristat ethyl', 'Quinidine', 'Moderate', 'Coadministration with telotristat ethyl may decrease the plasma concentrations of drugs that are substrates of CYP450 3A4 and/or 2B6 isoenzymes.  The proposed mechanism is accelerated clearance due to induction of CYP450 3A4 and/or 2B6 (in vitro) isoenzymes by telotristat ethyl.', NULL, 'When drugs that are known substrates of CYP450 3A4 and/or 2B6 are coadministered with telotristat ethyl, the possibility of a diminished therapeutic response to those drugs should be considered.', NULL, 'When drugs that are known substrates of CYP450 3A4 and/or 2B6 are coadministered with telotristat ethyl, the possibility of a diminished therapeutic response to those drugs should be considered.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs, particularly those with a narrow therapeutic range, whenever telotristat ethyl is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298381/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Xermelo (telotristat ethyl)." Lexicon Pharmaceuticals, Inc.  (2017):', 1767363028);
