INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251674, 'Dienogest', 'Aprepitant', 'Moderate', 'Aprepitant and its prodrug, fosaprepitant, may reduce the efficacy of low-dose hormonal contraceptives during and for up to 28 days after treatment.  The proposed mechanism is aprepitant induction of CYP450 3A4, the isoenzyme partially responsible for the metabolic clearance of contraceptive hormones.', NULL, 'Women using low-dose hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with aprepitant or fosaprepitant.', NULL, 'Women using low-dose hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with aprepitant or fosaprepitant.  Alternative or additional methods of birth control is recommended during treatment with aprepitant and for one month following the last dose of aprepitant.  Data are not available for single-dosing of aprepitant 40 mg.  However, since the timing of aprepitant administration relative to ovulation could cause contraceptive failure, the same precaution as for the 125 mg/80 mg regimen should be observed.  Intrauterine systems are unlikely to be significantly affected because of their local action.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298394/', '[1] "Product Information. Emend (aprepitant)." Merck & Co., Inc  (2003):[2] "Product Information. Emend for Injection (fosaprepitant)." Merck & Co., Inc  (2008):[3] Faculty of Sexual & Reproductive Healthcare "FSRH Clinical Guidance: Drug Interactions with Hormonal Contraception.  file:///C:/Users/df033684/Downloads/ceuguidancedruginteractionshormonal.pdf"   (2016):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251675, 'Dienogest', 'Armodafinil', 'Moderate', 'Coadministration with modafinil (the racemate) or armodafinil (the R-enantiomer) may decrease the plasma concentrations of ethinyl estradiol and other contraceptive hormones.  The mechanism involves induction of CYP450 3A4-mediated metabolism by modafinil and armodafinil.', NULL, 'Women using hormonal contraceptives (including depot and implantable contraceptives) should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with modafinil or armodafinil.', NULL, 'Women using hormonal contraceptives (including depot and implantable contraceptives) should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with modafinil or armodafinil.  Alternative or additional methods of birth control are recommended during and for one month after treatment with modafinil or armodafinil.  Intrauterine systems are unlikely to be significantly affected because of their local action.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298395/', '[1] "Product Information. Provigil (modafinil)." Cephalon, Inc  (2001):[2] Robertson P, Decory HH, Madan A, Parkinson A "In vitro inhibition and induction of human hepatic cytochrome P450 enzymes by modafinil." Drug Metab Dispos 28 (2000):  664-71[3] Robertson P Jr, Hellriegel ET, Arora S, Nelson M "Effect of modafinil on the pharmacokinetics of ethinyl estradiol and triazolam in healthy volunteers." Clin Pharmacol Ther 71 (2002):  46-56[4] "Product Information. Nuvigil (armodafinil)." Cephalon Inc  (2007):[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] Faculty of Sexual & Reproductive Healthcare "FSRH Clinical Guidance: Drug Interactions with Hormonal Contraception.  file:///C:/Users/df033684/Downloads/ceuguidancedruginteractionshormonal.pdf"   (2016):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251676, 'Dienogest', 'Asparaginase Erwinia chrysanthemi', 'Moderate', 'Coadministration with another thiazolidinedione, troglitazone, has been shown to decrease the plasma concentrations of contraceptive hormones.  The proposed mechanism is induction of hormone metabolism via CYP450 3A4.', NULL, 'The use of oral contraceptives during asparaginase treatment is not recommended.', NULL, 'The use of oral contraceptives during asparaginase treatment is not recommended.  Other non-oral contraceptive methods should be used in women of childbearing potential.  According to the manufacturer, females of reproductive potential should be advised to use effective contraception during asparaginase treatment and for at least 3 to 6 months after the last dose.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298396/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Asparlas (calaspargase pegol)." Servier  (2019):', 1767363028);
