INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251688, 'Human C1-esterase inhibitor', 'Dienogest', 'Moderate', 'Thromboembolism may occur during treatment with C1 esterase inhibitors, and concurrent use of other agents that are also associated with this adverse effect can potentiate the risk.', NULL, 'Caution is advised when C1 esterase inhibitors are used with other drugs that have thrombogenic effects such as androgens, estrogens, erythropoiesis-stimulating agents, or selective estrogen receptor modulators.', NULL, 'Caution is advised when C1 esterase inhibitors are used with other drugs that have thrombogenic effects such as androgens, estrogens, erythropoiesis-stimulating agents, or selective estrogen receptor modulators.  Close monitoring for thromboembolic events is recommended during and after administration of C1 esterase inhibitors.  Patients should be advised to seek medical attention if they develop potential signs and symptoms of thromboembolism such as chest pain; shortness of breath; rapid pulse; pain, swelling, and/or discoloration in an arm or leg; and numbness or weakness on one side of the body.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298410/', '[1] "Product Information. Cinryze (C1 esterase inhibitor, human)." ViroPharma Incorporated  (2022):[2] "Product Information. Berinert (C1 esterase inhibitor, human)." CSL Behring LLC  (2022):[3] Gandhi PK, Gentry WM, Bottorff MB "Thrombotic events associated with C1 esterase inhibitor products in patients with hereditary angioedema: investigation from the United States Food and Drug Administration adverse event reporting system database." Pharmacotherapy 32 (2012):