INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251697, 'Dienogest', 'Cholestyramine', 'Moderate', 'Coadministration with bile-acid binding resins such as cholestyramine may decrease the plasma concentrations and therapeutic effects of estrogens and progestins.  The proposed mechanism involves delaying or reducing the absorption of concomitant orally administered drugs by cholestyramine.', NULL, 'Estrogens and/or progestin-containing oral medications should be administered at least one hour before or four to six hours after bile-acid binding resins such as cholestyramine, colestipol, and colesevelam.', NULL, 'Estrogens and/or progestin-containing oral medications should be administered at least one hour before or four to six hours after bile-acid binding resins such as cholestyramine, colestipol, and colesevelam.  Patients receiving this combination should be advised to closely follow dosing schedules to prevent loss of therapeutic effects.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298419/', '[1] "Product Information. Questran (cholestyramine)." Par Pharmaceutical Inc  (2002):[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Agencia Espala de Medicamentos y Productos Sanitarios Healthcare "Centro de informaci online de medicamentos de la AEMPS - CIMA.  https://cima.aemps.es/cima/publico/home.html"   (2008):[4] Cerner Multum, Inc. "Canadian Product Information." O 0 (2015):[5] Underhill KL, Rotter BA, Thompson BK, Prelusky DB, Trenholm HL "Effectiveness of cholestyramine in the detoxification of zearalenone as determined in mice." Bull Environ Contam Toxicol 54 (1995):  128-34[6] Arts CJM, Govers CARL, van den Berg H, Wolters MGE, van Leeuwen P, Thijssen JHH "In vitro binding of estrogens by dietary fiber and the in vivo apparent digestibility tested in pigs." J Steroid Biochem Mol Biol 38 (1991):  621-8[7] "Product Information. Nextstellis (drospirenone-estetrol)." Mayne Pharma  (2021):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251698, 'Dienogest', 'Cladribine', 'Moderate', 'Coadministration with cladribine may reduce the efficacy of hormonal contraceptives.', NULL, 'Although the clinical significance of this interaction is unknown, caution is advised when cladribine is prescribed concomitantly with hormonal contraceptives because of the potential to cause fetal harm.', NULL, 'Although the clinical significance of this interaction is unknown, caution is advised when cladribine is prescribed concomitantly with hormonal contraceptives because of the potential to cause fetal harm.  Women should avoid becoming pregnant during treatment with cladribine and for at least 6 months after the last dose in each treatment course.  Women using hormonal contraceptives should add a barrier method during cladribine dosing and for at least 4 weeks after the last dose in each treatment cycle.  In addition, male patients should take precautions to prevent pregnancy of their partner during cladribine dosing and for at least 6 months after the last dose in each treatment course.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298420/', '[1] "Product Information. Leustatin (cladribine)." Ortho Biotech Inc  (2001):[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0', 1767363028);
