INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251701, 'Dienogest', 'Clozapine', 'Moderate', 'Limited data suggest that coadministration with contraceptive hormones may increase the plasma concentrations of clozapine.  The exact mechanism of interaction is unknown but may involve inhibition of clozapine metabolism via CYP450 1A2, 2C19 and/or 3A4.', NULL, 'Patients treated concomitantly with hormonal contraceptives may require a lower dosage of clozapine to control psychotic symptoms.', NULL, 'Patients treated concomitantly with hormonal contraceptives may require a lower dosage of clozapine to control psychotic symptoms.  Pharmacologic response to clozapine should be monitored more closely whenever an oral contraceptive is added to or withdrawn from therapy in patients stabilized on their existing antipsychotic regimen, and the clozapine dosage adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298423/', '[1] Gabbay V, O''Dowd MA, Mamamtavrishvili M, Asnis GM "Clozapine and Oral Contraceptives: A Possible Drug Interaction." J Clin Psychopharmacol 22 (2002):  621-622', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251702, 'Dienogest', 'Cobicistat', 'Moderate', 'Coadministration with cobicistat and elvitegravir may alter the plasma concentrations of contraceptive hormones.  The exact mechanism of interaction has not been described.', NULL, 'The potential risks and benefits of using norgestimate-containing contraceptives in combination with cobicistat and elvitegravir should be considered, particularly in women who have risk factors for insulin resistance, dyslipidemia, acne, and venous thrombosis.', NULL, 'The potential risks and benefits of using norgestimate-containing contraceptives in combination with cobicistat and elvitegravir should be considered, particularly in women who have risk factors for insulin resistance, dyslipidemia, acne, and venous thrombosis.  Coadministration of cobicistat and elvitegravir with other hormonal contraceptives (e.g., contraceptive patch, contraceptive vaginal ring, or injectable contraceptives), oral contraceptives containing progestogens other than norgestimate, or less than 25 mcg of ethinyl estradiol has not been studied and is not recommended.  Some authorities recommend that hormonal contraceptives containing at least 30 mcg of ethinyl estradiol with norgestimate should be used in combination with the multi-ingredient antiretroviral formulations containing elvitegravir/cobicistat/emtricitabine/tenofovir disoproxil fumarate or elvitegravir/cobicistat/emtricitabine/tenofovir alafenamide.  However, the long term effects of increased progesterone exposure are not known.  Alternative, nonhormonal methods of contraception may be considered.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298424/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Stribild (cobicistat/elvitegravir/emtricitabine/tenofov)." Gilead Sciences  (2012):', 1767363028);
