INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251705, 'Dienogest', 'Conestat alfa', 'Moderate', 'Thromboembolism may occur during treatment with C1 esterase inhibitors, and concurrent use of other agents that are also associated with this adverse effect can potentiate the risk.', NULL, 'Caution is advised when C1 esterase inhibitors are used with other drugs that have thrombogenic effects such as androgens, estrogens, erythropoiesis-stimulating agents, or selective estrogen receptor modulators.', NULL, 'Caution is advised when C1 esterase inhibitors are used with other drugs that have thrombogenic effects such as androgens, estrogens, erythropoiesis-stimulating agents, or selective estrogen receptor modulators.  Close monitoring for thromboembolic events is recommended during and after administration of C1 esterase inhibitors.  Patients should be advised to seek medical attention if they develop potential signs and symptoms of thromboembolism such as chest pain; shortness of breath; rapid pulse; pain, swelling, and/or discoloration in an arm or leg; and numbness or weakness on one side of the body.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298427/', '[1] "Product Information. Cinryze (C1 esterase inhibitor, human)." ViroPharma Incorporated  (2022):[2] "Product Information. Berinert (C1 esterase inhibitor, human)." CSL Behring LLC  (2022):[3] Gandhi PK, Gentry WM, Bottorff MB "Thrombotic events associated with C1 esterase inhibitor products in patients with hereditary angioedema: investigation from the United States Food and Drug Administration adverse event reporting system database." Pharmacotherapy 32 (2012):  902-9[4] "Product Information. Ruconest (conestat alfa)." Valeant Pharmaceuticals  (2015):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251706, 'Dienogest', 'Cyclosporine', 'Moderate', 'Coadministration with some estrogens or progestins may increase the plasma concentrations of cyclosporine.  The risk of cyclosporine-induced nephrotoxicity, hepatotoxicity, and neurotoxicity may be increased.  The proposed mechanism is competitive inhibition of cyclosporine metabolism via CYP450 3A4, of which estrogens and progestins are also substrates.', NULL, 'Caution is advised during concomitant therapy with cyclosporine.', NULL, 'Caution is advised during concomitant therapy with cyclosporine.  Cyclosporine blood levels as well as renal and hepatic function should be checked frequently and the dosage adjusted accordingly, particularly following initiation or discontinuation of hormonal contraceptives in patients who are stabilized on their cyclosporine regimen.  Patients should be advised to notify their physician if they experience signs and symptoms of hepatotoxicity (e.g., fever, rash, anorexia, nausea, vomiting, fatigue, right upper quadrant pain, dark urine, jaundice) or neurotoxicity (e.g., tremor, convulsions).', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298428/', '[1] Maurer G "Metabolism of cyclosporine." Transplant Proc 17 (1985):  19-26[2] Deray G, Le Hoang P, Cacoub P, et al. "Oral contraceptive interaction with cyclosporin." Lancet 1 (1987):  158-9', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251707, 'Dienogest', 'Dabrafenib', 'Major', 'Coadministration with dabrafenib may decrease the plasma concentrations and efficacy of contraceptive hormones.  The mechanism is dabrafenib induction of CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of sex hormones.', NULL, 'Hormonal contraceptives, including oral, injectable, transdermal, and implantable forms, may not be reliable during concomitant therapy with dabrafenib.', NULL, 'Hormonal contraceptives, including oral, injectable, transdermal, and implantable forms, may not be reliable during concomitant therapy with dabrafenib.  Because use of dabrafenib may cause fetal harm, it is particularly important that patients not become pregnant during treatment.  Female patients of reproductive potential should use a highly effective, non-hormonal method of contraception during treatment and for 4 weeks after treatment.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.  Intrauterine systems are unlikely to be significantly affected because of their local action.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298429/', '[1] "Product Information. Tafinlar (dabrafenib)." GlaxoSmithKline  (2013):[2] Faculty of Sexual & Reproductive Healthcare "FSRH Clinical Guidance: Drug Interactions with Hormonal Contraception.  file:///C:/Users/df033684/Downloads/ceuguidancedruginteractionshormonal.pdf"   (2016):', 1767363028);
