INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251709, 'Dienogest', 'Deferasirox', 'Moderate', 'Coadministration with deferasirox may decrease the plasma concentrations of contraceptive hormones.  The proposed mechanism is deferasirox induction of CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of sex hormones.', NULL, 'Hormonal contraceptives agents may not be reliable when used with deferasirox.', NULL, 'Hormonal contraceptives agents may not be reliable when used with deferasirox.  Female patients of reproductive potential receiving hormonal contraceptives should be counseled to use alternative or additional methods of birth control during treatment with deferasirox.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298431/', '[1] "Product Information. Desferal (deferoxamine)." Novartis Pharmaceuticals  (2001):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251710, 'Dienogest', 'Valproic acid', 'Moderate', 'A case report suggests that estrogens or progestins may decrease the serum concentrations and pharmacologic effects of valproic acid (VPA).  The proposed mechanism is induction of hepatic glucuronidation by sex hormones.', NULL, 'Pharmacologic response and serum valproic levels should be monitored more closely whenever estrogen- and/or progestin-containing drugs are added to or withdrawn from therapy, and the valproic acid dosage adjusted as necessary.', NULL, 'Pharmacologic response and serum valproic levels should be monitored more closely whenever estrogen- and/or progestin-containing drugs are added to or withdrawn from therapy, and the valproic acid dosage adjusted as necessary.  Patients should be advised to contact their physician if they experience loss of seizure control or symptoms of valproic acid toxicity such as tremors, ataxia, nystagmus, increased seizures, and changes in mental status.  In patients receiving oral contraceptives, gradual transient increases in valproic acid levels will likely occur during the pill-free week for women not also taking an enzyme-inducing drug (e.g., carbamazepine, phenytoin, phenobarbital, primidone, rifampin).  The increase in valproic acid levels will be greater if the dose of valproic acid is increased in the few days before or during the pill-free week.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298432/', '[1] "Product Information. Depakene (valproic acid)." Abbott Pharmaceutical  (2001):[2] "Product Information. Depakote (divalproex sodium)." Abbott Pharmaceutical  (2001):[3] Crawford P, Chadwick D, Cleland P, Tjia J, Cowie A, Back DJ, Orme ML "The lack of effect of sodium valproate on the pharmacokinetics of oral contraceptive steroids." Contraception 33 (1986):  23-9[4] "Product Information. Depacon (valproic acid)." Abbott Pharmaceutical  (2001):[5] Herzog AG, Farina EL, Blum AS "Serum valproate levels with oral contraceptive use." Epilepsia 46 (2005):  970-1', 1767363028);
