INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251717, 'Enasidenib', 'Dienogest', 'Moderate', 'Coadministration with enasidenib may alter the plasma concentrations of hormonal contraceptives.  In vitro, enasidenib has been found to both inhibit and induce CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of contraceptive hormones.', NULL, 'Because use of enasidenib may be associated with fetal harm, it is important that patients not become pregnant during treatment.', NULL, 'Because use of enasidenib may be associated with fetal harm, it is important that patients not become pregnant during treatment.  It may be advisable to monitor patients for potentially altered effects of their hormonal contraceptive during and after treatment with enasidenib.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.  Intrauterine systems are unlikely to be significantly affected because of their local action.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298439/', '[1] "Product Information. Idhifa (enasidenib)." Celgene Corporation  (2017):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251718, 'Encorafenib', 'Dienogest', 'Major', 'Coadministration with encorafenib may decrease the plasma concentrations and efficacy of contraceptive hormones.  The proposed mechanism is induction of CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of sex hormones.', NULL, 'Hormonal contraceptives, including oral, injectable, transdermal, and implantable forms, may not be reliable during concomitant therapy with encorafenib.', NULL, 'Hormonal contraceptives, including oral, injectable, transdermal, and implantable forms, may not be reliable during concomitant therapy with encorafenib.  Because encorafenib can cause fetal harm, it is particularly important that patients not become pregnant during treatment.  Therefore, females of reproductive potential should use an effective, nonhormonal method of contraception during treatment and for 2 weeks after the final dose of encorafenib.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.  Intrauterine systems are unlikely to be significantly affected because of their local action.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298440/', '[1] "Product Information. Braftovi (encorafenib)." Array BioPharma Inc.  (2018):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251719, 'Entrectinib', 'Dienogest', 'Moderate', 'Coadministration with entrectinib may render systemic hormonal contraceptives ineffective.  The mechanism for the potential interaction has not been established and there are no clinical data regarding the use of entrectinib with hormonal contraceptives.', NULL, 'Until further data are available, females of reproductive potential should be advised to use effective non-hormonal contraception during treatment and for 5 weeks after the final dose.', NULL, 'Until further data are available, females of reproductive potential should be advised to use effective non-hormonal contraception during treatment and for 5 weeks after the final dose.  In addition, men receiving entrectinib should use highly effective contraceptive methods during treatment and for 3 months after the final dose.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298441/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Rozlytrek (entrectinib)." Genentech  (2019):', 1767363028);
