INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251728, 'Flibanserin', 'Dienogest', 'Moderate', 'Concomitant use of hormonal contraceptives has been associated with increased adverse effects of flibanserin.  The mechanism of interaction has not been established, but may partially involve inhibition of flibanserin metabolism.  Flibanserin is primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 2C19.  Some oral contraceptives are considered weak inhibitors of these isoenzymes.', NULL, 'Caution is advised when flibanserin is used with hormonal contraceptives.', NULL, 'Caution is advised when flibanserin is used with hormonal contraceptives.  Patients should be monitored for signs and symptoms of hypotension, syncope, and central nervous system depression.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298450/', '[1] "Product Information. Addyi (flibanserin)." Sprout Pharmaceuticals  (2015):', 1767363028);
