INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251736, 'Glycerol phenylbutyrate', 'Dienogest', 'Moderate', 'Coadministration with glycerol phenylbutyrate may decrease the systemic exposure to and therapeutic efficacy of drugs that are substrates of CYP450 3A4.  Glycerol phenylbutyrate, its active moiety phenylbutyrate, and active metabolite phenylacetic acid are considered weak CYP450 3A4 inducers in vivo.', NULL, 'Concomitant use of glycerol phenylbutyrate with sensitive CYP450 3A4 substrates (e.', NULL, 'Concomitant use of glycerol phenylbutyrate with sensitive CYP450 3A4 substrates (e.g., oral midazolam) or CYP450 3A4 substrates with a narrow therapeutic index (e.g., alfentanil, oral contraceptives, quinidine, and cyclosporine) should be done with caution and monitoring for reduced efficacy.  Dosage adjustment as well as clinical and laboratory monitoring may be appropriate whenever glycerol phenylbutyrate is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298458/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Ravicti (glycerol phenylbutyrate)." Hyperion Therapeutics Inc  (2013):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251737, 'Golimumab', 'Dienogest', 'Moderate', 'Plasma concentrations of drugs that are CYP450 substrates may decrease following the initiation of interleukin (IL) inhibitors, tumor necrosis factor (TNF) blockers, or interferon (IFN) inhibitors in patients with chronic inflammatory diseases.  Because the formation of hepatic CYP450 enzymes is down-regulated during infection and chronic inflammation by increased levels of certain cytokines (e.g., interleukins-1, -6, and -10; tumor necrosis factor alpha; interferons), treatment targeting these cytokines may restore or normalize CYP450 enzyme levels resulting in increased metabolism of these drugs.', NULL, 'Caution is advised when treatments targeting cytokines such as interleukins, tumor necrosis factors, or interferons are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges (e.', NULL, 'Caution is advised when treatments targeting cytokines such as interleukins, tumor necrosis factors, or interferons are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges (e.g., antiarrhythmics, anticonvulsants, immunosuppressants, theophylline) or sensitive substrates where decreases in plasma levels may be significant or undesirable (e.g., oral contraceptives, statins, benzodiazepines, opioids).  Clinical and/or laboratory monitoring should be considered following the initiation or withdrawal of such treatments, and the dosage(s) of the CYP450 substrate(s) adjusted accordingly.  Clinicians should note that the effects of IL inhibitors, TNF blockers, and IFN inhibitors on CYP45