INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251739, 'Guselkumab', 'Dienogest', 'Moderate', 'Plasma concentrations of drugs that are CYP450 substrates may decrease following the initiation of interleukin (IL) inhibitors, tumor necrosis factor (TNF) blockers, or interferon (IFN) inhibitors in patients with chronic inflammatory diseases.  Because the formation of hepatic CYP450 enzymes is down-regulated during infection and chronic inflammation by increased levels of certain cytokines (e.g., interleukins-1, -6, and -10; tumor necrosis factor alpha; interferons), treatment targeting these cytokines may restore or normalize CYP450 enzyme levels resulting in increased metabolism of these drugs.', NULL, 'Caution is advised when treatments targeting cytokines such as interleukins, tumor necrosis factors, or interferons are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges (e.', NULL, 'Caution is advised when treatments targeting cytokines such as interleukins, tumor necrosis factors, or interferons are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges (e.g., antiarrhythmics, anticonvulsants, immunosuppressants, theophylline) or sensitive substrates where decreases in plasma levels may be significant or undesirable (e.g., oral contraceptives, statins, benzodiazepines, opioids).  Clinical and/or laboratory monitoring should be considered following the initiation or withdrawal of such treatments, and the dosage(s) of the CYP450 substrate(s) adjusted accordingly.  Clinicians should note that the effects of IL inhibitors, TNF blockers, and IFN inhibitors on CYP450 activities may persist for several weeks after stopping therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298461/', '[1] "Product Information. Remicade (infliximab)." Centocor Inc  (2001):[2] "Product Information. Amevive (alefacept)." Biogen  (2003):[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Arcalyst (rilonacept)." Regeneron Pharmaceuticals Inc  (2008):[5] "Product Information. Stelara (ustekinumab)." Centocor Inc  (2009):[6] "Product Information. Simponi (golimumab)." Centocor Inc  (2009):[7] "Product Information. Ilaris (canakinumab)." Novartis Pharmaceuticals  (2009):[8] "Product Information. Actemra (tocilizumab)." Genentech  (2010):[9] "Product Information. Sylvant (siltuximab)." Janssen Biotech, Inc.  (2014):[10] "Product Information. Cosentyx (secukinumab)." Novartis Pharmaceuticals  (2015):[11] "Product Information. Taltz Autoinjector (ixekizumab)." Eli Lilly and Company  (2016):[12] "Product Information. Kevzara (sarilumab)." sanofi-aventis  (2017):[13] "Product Information. Ilumya (tildrakizumab)." Merck & Co., Inc  (2018):[14] "Product Information. Gamifant (emapalumab)." Sobi Inc  (2018):[15] "Product Information. Skyrizi (risankizumab)." AbbVie US LLC  (2019):', 1767363028);
