INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251782, 'Dienogest', 'Nelfinavir', 'Moderate', 'Nelfinavir may decrease the plasma concentrations of contraceptive hormones during chronic coadministration.  The proposed mechanism is nelfinavir induction of the hepatic metabolism of sex hormones.', NULL, 'When using Nelfinavir and birth control pills together, we recommend additional contraception.', NULL, 'When using Nelfinavir and birth control pills together, we recommend additional contraception.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298507/', '[1] "Product Information. Viracept (nelfinavir)." Agouron Pharma Inc  (2001):[2] "FFPRHC Guidance (April 2005). Drug interactions with hormonal contraception." J Fam Plann Reprod Health Care 31 (2005):  139-51[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Faculty of Sexual & Reproductive Healthcare "FSRH Clinical Guidance: Drug Interactions with Hormonal Contraception.  file:///C:/Users/df033684/Downloads/ceuguidancedruginteractionshormonal.pdf"   (2016):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251783, 'Dienogest', 'Neratinib', 'Moderate', 'Coadministration with neratinib may reduce the efficacy of hormonal contraceptives.  The mechanism for the potential interaction has not been established.', NULL, 'Although the clinical significance of this interaction is unknown, caution is advised when neratinib is prescribed concomitantly with hormonal contraceptives because of the potential to cause fetal harm.', NULL, 'Although the clinical significance of this interaction is unknown, caution is advised when neratinib is prescribed concomitantly with hormonal contraceptives because of the potential to cause fetal harm.  Women should avoid becoming pregnant during treatment with neratinib and for up to 1 month after discontinuing treatment.  Women using hormonal contraceptives should add a barrier method.  In addition, men receiving neratinib should use a barrier method of contraception during treatment and for 3 months after discontinuing treatment.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298508/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251784, 'Dienogest', 'Nevirapine', 'Moderate', 'Coadministration with nevirapine may decrease the plasma concentrations of contraceptive hormones.  The proposed mechanism is nevirapine induction of CYP450 3A4, the isoenzyme partially responsible for the metabolic clearance of sex hormones and other steroids.', NULL, 'ADDITIONAL CONTRACEPTION RECOMMENDED:  Coadministration with nevirapine may decrease the plasma concentrations of contraceptive hormones.', NULL, 'ADDITIONAL CONTRACEPTION RECOMMENDED:  Coadministration with nevirapine may decrease the plasma concentrations of contraceptive hormones.  The proposed mechanism is nevirapine induction of CYP450 3A4, the isoenzyme partially responsible for the metabolic clearance of sex hormones and other steroids.  In ten HIV-positive subjects, nevirapine (200 mg once a day for 2 weeks followed by 200 mg twice a day for 2 weeks) decreased the area under the plasma concentration-time curve (AUC) of ethinyl estradiol (administered as ethinyl estradiol-norethindrone 0.035 mg-1 mg) by 20% compared to administration of the oral contraceptive alone.  The AUC and peak plasma concentration (Cmax) of norethindrone decreased by 19% and 16%, respectively.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298509/', '[1] "Product Information. Viramune (nevirapine)." Boehringer-Ingelheim  (2001):[2] Back D, Gibbons S, Khoo S "Pharmacokinetic drug interactions with nevirapine." J Acquir Immune Defic Syndr 34 Suppl 1 (2003):  S8-14[3] Faculty of Sexual & Reproductive Healthcare "FSRH Clinical Guidance: Drug Interactions with Hormonal Contraception.  file:///C:/Users/df033684/Downloads/ceuguidancedruginteractionshormonal.pdf"   (2016):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251785, 'Dienogest', 'Olaparib', 'Moderate', 'Coadministration with olaparib may decrease the plasma concentrations and efficacy of contraceptive hormones.  The proposed mechanism is olaparib induction of CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of sex hormones.', NULL, 'Some authorities recommend that women avoid pregnancy during olaparib treatment due to its potential teratogenic effects.', NULL, 'Some authorities recommend that women avoid pregnancy during olaparib treatment due to its potential teratogenic effects.  In women of childbearing potential, alternative or additional non-hormonal methods of birth control should be considered during treatment and for one month after receiving the last dose of olaparib.  Regular pregnancy tests are also recommended.  Intrauterine systems are unlikely to be significantly affected because of their local action.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298510/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Faculty of Sexual & Reproductive Healthcare "FSRH Clinical Guidance: Drug Interactions with Hormonal Contraception.  file:///C:/Users/df033684/Downloads/ceuguidancedruginteractionshormonal.pdf"   (2016):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (251786, 'Dienogest', 'Oxcarbazepine', 'Major', 'Coadministration with certain anticonvulsants such as carbamazepine, eslicarbazepine, oxcarbazepine, phenobarbital, phenytoin, and primidone may reduce the efficacy of contraceptive hormones.', NULL, 'Women using hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with enzyme-inducing anticonvulsants.', NULL, 'Women using hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with enzyme-inducing anticonvulsants.  Alternative or additional methods of birth control should be used during and for at least two weeks after short-term and 4 weeks after long-term (greater than 4 weeks) anticonvulsant therapy.  If a combination oral contraceptive pill is used, a regimen containing at least 50 mcg of ethinyl estradiol per day or equivalent should be considered.  Although breakthrough bleeding is not necessarily indicative of low ethinyl estradiol serum levels or increased risk of ovulation, some clinicians suggest that women who experience breakthrough bleeding during enzyme-inducing therapy may be prescribed an increased dose of ethinyl estradiol above 50 mcg daily by combining more than one formulation of contraceptive pill if necessary.  For emergency contraception in patients who have used an hepatic enzyme inducer in the past 4 weeks, a non-hormonal emergency contraceptive (e.g., copper intrauterine device) is considered preferable.  If this is not possible, some authorities recommend that the usual dose of levonorgestrel (1.5 mg) should be doubled to 3 mg and taken as a single dose as soon as possible (within 72 hours of unprotected sexual intercourse).  However, there are no data on efficacy, compliance, or side effects of this regimen.  For women with the etonogestrel subdermal implant, the addition of a barrier method is recommended during concomitant use and for 28 days after discontinuation of hepatic enzyme inducing drugs.  It is recommended to remove the implant and to prescribe a nonhormonal method in women who require long-term treatment with hepatic enzyme inducing drugs.  No precautions or recommendations are available for women using hormone-releasing intrauterine systems, but a significant interaction with these systems is thought to be unlikely due to their local action.  Injectable progestin-only contraceptives are also thought to be unaffected by enzyme-inducing drugs.', 'Antagonism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298512/', '[1] Crawford P, Chadwick DJ, Martin C, et al. "The interaction of phenytoin and carbamazepine with combined oral contraceptive steroids." Br J Clin Pharmacol 30 (1990):  892-6[2] Odlind V, Olsson SE "Enhanced metabolism of levonorgestrel during phenytoin treatment in a woman with norplant implants." Contraception 33 (1986):  257-61[3] Baciewicz AM "Oral contraceptive drug interactions." Ther Drug Monit 7 (1985):  26-35[4] Back DJ, Bates M, Bowden A, et al. "The interaction of phenobarbital and other anticonvulsants with oral contraceptive steroid therapy." Contraception 22 (1980):  495-503[5] Dossetor J "Drug interactions with oral contraceptives." Br Med J 4 (1975):  467-8[6] Furlan AJ, Rothner AD "Anti-epileptic drugs and failure of oral contraceptives." Lancet 1 (1974):  1113[7] Coulam CB, Annegers JF "Do anticonvulsants reduce the efficacy of oral contraceptives?" Epilepsia 20 (1979):  519-26[8] Szoka PR, Edgren RA "Drug interactions with oral contraceptives: compilation and analysis of an adverse experience r