        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1474698, 'Pazopanib', 'Artesunate', 'Moderate', 'Coadministration with potent inhibitors of uridine diphosphate glucuronosyltransferase (UGT) may increase the peak plasma concentration (Cmax) and systemic exposure (AUC) of dihydroartemisinin (DHA), an active metabolite of artesunate and substrate of UGT 1A9 or 2B7. Increased exposure to artesunate may increase the risk of adverse effects such as anemia, increased liver transaminases, thrombocytopenia, hyperbilirubinemia, acute renal failure, and leukocytosis.', NULL, 'Monitoring for adverse reactions is recommended if artesunate is coadministered with potent UGT inhibitors.', NULL, 'Monitoring for adverse reactions is recommended if artesunate is coadministered with potent UGT inhibitors. Dosage adjustments or alternative treatments may be required based on clinical response and tolerance.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
