        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1474865, 'Selexipag', 'Pazopanib', 'Moderate', 'Coadministration with inhibitors of CYP450 2C8 may increase the plasma concentrations of selexipag and its active metabolite. The interaction has not been studied; however, both selexipag and the metabolite undergo oxidative metabolism by CYP450 2C8 and 3A4.', NULL, 'Caution is advised when selexipag is prescribed with CYP450 2C8 inhibitors.', NULL, 'Caution is advised when selexipag is prescribed with CYP450 2C8 inhibitors. Dosage adjustments as well as clinical and laboratory monitoring of selexipag should be considered whenever a CYP450 2C8 inhibitor is added to or withdrawn from therapy. Patients should be monitored for potentially increased side effects such as diarrhea, nausea, vomiting, flushing, anemia, and decreased appetite.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
