    ) VALUES (1474875, 'Sirolimus', 'Pazopanib', 'Moderate', 'Coadministration of substrates of CYP450 3A4, 2D6, or 2C8 with pazopanib may increase their plasma concentrations. The mechanism is inhibition of CYP450-mediated metabolism by pazopanib.', NULL, 'Due to the potential for adverse effects, the concomitant use of pazopanib with drugs that have narrow therapeutic ranges and that are substrates of CYP450 3A4, 2D6, or 2C8 is not recommended.', NULL, 'Due to the potential for adverse effects, the concomitant use of pazopanib with drugs that have narrow therapeutic ranges and that are substrates of CYP450 3A4, 2D6, or 2C8 is not recommended.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
