INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (252167, 'Grazoprevir', 'Lovastatin', 'Major', 'Coadministration with elbasvir-grazoprevir may significantly increase the plasma concentrations of HMG-CoA reductase inhibitors (statins) that are substrates of the CYP450 3A4 isoenzyme and/or breast cancer resistance protein (BCRP) transporter.  Grazoprevir is reported to be a relatively weak inhibitor of CYP450 3A4 in vivo, and both elbasvir and grazoprevir are inhibitors of BCRP at the intestinal level in humans.', NULL, 'Caution is advised when statins such as atorvastatin, rosuvastatin, fluvastatin, lovastatin, and simvastatin are prescribed with elbasvir-grazoprevir.', NULL, 'Caution is advised when statins such as atorvastatin, rosuvastatin, fluvastatin, lovastatin, and simvastatin are prescribed with elbasvir-grazoprevir.  Atorvastatin dosage should not exceed 20 mg/day and rosuvastatin dosage should not exceed 10 mg/day according to the product labeling for elbasvir-grazoprevir.  The lowest effective dose of fluvastatin, lovastatin, or simvastatin should be used; some authorities recommend a maximum daily dose of 20 mg of these statins.  In contrast, pitavastatin and pravastatin do not have clinically relevant interactions with elbasvir-grazoprevir, and no dosage adjustments are necessary.  All patients receiving statin therapy should be advised to promptly report any unexplained muscle pain, tenderness or weakness, particularly if accompanied by fever, malaise and/or dark colored urine.  Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/299126/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Zepatier (elbasvir-grazoprevir)." Merck & Co., Inc  (2016):', 1767365211);
