INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (252254, 'Grazoprevir', 'Tacrolimus', 'Moderate', 'Coadministration with elbasvir-grazoprevir may increase the blood concentrations of tacrolimus.  The proposed mechanism is inhibition of the CYP450 3A4-mediated metabolism of tacrolimus by grazoprevir, a weak inhibitor of the isoenzyme.', NULL, 'Given the narrow therapeutic index and the large interindividual variability in the pharmacokinetics of tacrolimus, caution is advised if the drug is used in combination with elbasvir-grazoprevir.', NULL, 'Given the narrow therapeutic index and the large interindividual variability in the pharmacokinetics of tacrolimus, caution is advised if the drug is used in combination with elbasvir-grazoprevir.  Renal function and tacrolimus blood levels should be monitored more closely whenever elbasvir-grazoprevir is added to or discontinued from therapy, and the tacrolimus dosage adjusted as necessary.  Patients should be advised to contact their doctor if they experience potential signs and symptoms of tacrolimus toxicity such as fever, infection, diarrhea, tremor, headache, fatigue, lethargy, and changes in motor function, mental status, or sensory function.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/299230/', '[1] "Product Information. Zepatier (elbasvir-grazoprevir)." Merck & Co., Inc  (2016):', 1767365211);
