INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (252386, 'Sacubitril', 'Rosuvastatin', 'Moderate', 'Coadministration with sacubitril may increase the plasma concentrations of drugs that are substrates of organic anion transporting polypeptides (OATP) 1B1 and 1B3, such as some HMG-CoA reductase inhibitors (i.e., statins).  The proposed mechanism is sacubitril-mediated inhibition of hepatic uptake transporters OATP1B1 and/or OATP1B3.', NULL, 'Caution and close monitoring are advised if sacubitril must be used concomitantly with substrates of OATP1B1 and OATP1B3.', NULL, 'Caution and close monitoring are advised if sacubitril must be used concomitantly with substrates of OATP1B1 and OATP1B3.  All patients receiving statin therapy should be advised to promptly report any unexplained muscle pain, tenderness or weakness, particularly if accompanied by fever, malaise and/or dark colored urine.  Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/299401/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Canadian Pharmacists Association "e-CPS.  http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink"   (2006):', 1767365211);
