INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (252461, 'Velpatasvir', 'Lovastatin', 'Moderate', 'Coadministration with velpatasvir may increase the plasma concentrations of the HMG-CoA reductase inhibitors atorvastatin and simvastatin.  The proposed mechanism has not been delineated but may involve velpatasvir inhibition of the hepatic uptake transporter organic anion transporting polypeptide 1B (OATP1B), and the efflux transporters P-glycoprotein (P-gp), and/or breast cancer resistance protein (BCRP), of which atorvastatin and simvastatin are substrates.', NULL, 'The lowest dose of atorvastatin or simvastatin should be used when prescribed with sofosbuvir-velpatasvir, and the dosage titrated cautiously.', NULL, 'The lowest dose of atorvastatin or simvastatin should be used when prescribed with sofosbuvir-velpatasvir, and the dosage titrated cautiously.  Pravastatin may be considered as a substitute.  All patients receiving statin therapy should be advised to promptly report any unexplained muscle pain, tenderness, or weakness, particularly if accompanied by fever, malaise, and/or dark colored urine.  Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism, Distribution', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/299496/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Epclusa (sofosbuvir-velpatasvir)." Gilead Sciences  (2016):', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (252462, 'Velpatasvir', 'Lusutrombopag', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) may increase the plasma concentrations of lusutrombopag, which is a substrate of both efflux transporter proteins.', NULL, 'Caution is advised if lusutrombopag is prescribed in combination with P-gp and/or BCRP inhibitors.', NULL, 'Caution is advised if lusutrombopag is prescribed in combination with P-gp and/or BCRP inhibitors.  Pharmacologic response to lusutrombopag should be monitored more closely following initiation, discontinuation, or change of dosage of the concomitant P-gp or BCRP inhibitor.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/299497/', '[1] EMA. European Medicines Agency. European Union "EMA - List of medicines under additional monitoring.  http://www.ema.europa.eu/ema/index.jsp?curl=pages/regulation/document_listing/document_listing_000366.jsp&mid=WC0b01ac058067c852"   (2013):', 1767365211);
