INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (252471, 'Velpatasvir', 'Morphine', 'Major', 'Coadministration with P-glycoprotein (P-gp) inhibitors may increase the plasma concentrations of morphine and its risk of adverse effects, including hypotension, respiratory and CNS depression, profound sedation, coma, and death.  The proposed mechanism may involve inhibition of the intestinal P-gp efflux transporter, resulting in enhanced oral bioavailability of morphine, a P-gp substrate.', NULL, 'Caution is recommended whenever morphine, particularly orally administered morphine, is used concomitantly with a P-gp inhibitor.', NULL, 'Caution is recommended whenever morphine, particularly orally administered morphine, is used concomitantly with a P-gp inhibitor.  Close clinical and laboratory monitoring should be considered whenever a P-gp inhibitor is added to or withdrawn from therapy, and the morphine dosage adjusted as necessary.  Patients should be monitored closely for signs and symptoms of respiratory depression and sedation that may be greater than otherwise expected.  In addition, patients should be advised to avoid driving or operating hazardous machinery until they know how these medications affect them.', 'Absorption', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/299508/', '[1] "Product Information. MS Contin (morphine)." Purdue Frederick Company  (2002):[2] "Product Information. Kadian (morphine)." Astra-Zeneca Pharmaceuticals  (2001):[3] Kharasch ED, Hoffer C, Whittington D, Sheffels P "Role of P-glycoprotein in the intestinal absorption and clinical effects of morphine." Clin Pharmacol Ther 74 (2003):  543-54[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] Meissner K, Avram MJ, Yermolenka V, Francis AM, Blood J, Kharasch ED "Cyclosporine-inhibitable Blood-Brain Barrier Drug Transport Influences Clinical Morphine Pharmacodynamics." Anesthesiology  (2013):[7] Heiskanen T, Backman JT, Neuvonen M, Kontinen VK, Neuvonen PJ, Kalso E "Itraconazole, a potent inhibitor of P-glycoprotein, moderately increases plasma concentrations of oral morphine." Acta Anaesthesiol Scand 52 (2008):  1319-26', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (252472, 'Velpatasvir', 'Naldemedine', 'Moderate', 'Coadministration with moderate inhibitors of CYP450 3A4 and/or moderate or potent inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of naldemedine, which is primarily metabolized by CYP450 3A4 to nor-naldemedine and to a minor extent by UGT1A3 to naldemedine 3-G.  Both metabolites have demonstrated antagonistic activity for opioid receptors, but with less potency than the parent drug.  Naldemedine is also a substrate of the P-gp efflux transporter.', NULL, 'Caution is advised during concomitant use of naldemedine with moderate inhibitors of CYP450 3A4 and/or moderate or potent inhibitors of P-glycoprotein (P-gp).', NULL, 'Caution is advised during concomitant use of naldemedine with moderate inhibitors of CYP450 3A4 and/or moderate or potent inhibitors of P-glycoprotein (P-gp).  Patients should be closely monitored for potential opioid withdrawal symptoms as well as other adverse effects of naldemedine.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/299510/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Symproic (naldemedine)." Shionogi USA Inc  (2017):', 1767365211);
