INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1475317, 'Pitolisant', 'Peginterferon alfa-2b', 'Moderate', 'Coadministration with inhibitors of CYP450 2D6 may increase plasma concentrations of pitolisant. The proposed mechanism is decreased clearance of pitolisant due to inhibition of its metabolism via this isoenzyme. Clinically, high plasma levels of pitolisant may increase the risk of QT interval prolongation, which has been associated with ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317476, 'Caution and clinical monitoring are recommended if pitolisant is used in combination with CYP450 2D6 inhibitors. Patients should be monitored for clinical response and increased adverse effects such as headache, insomnia, nausea, anxiety, increased heart rate, QT prolongation, hallucinations, abdominal pain, upper respiratory tract infections and musculoskeletal pain, and the dosage of pitolisant adjusted as necessary in accordance with the product labeling.', 'Metabolism', 'Caution and clinical monitoring are recommended if pitolisant is used in combination with CYP450 2D6 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
