INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1475336, 'Telbivudine', 'Peginterferon alfa-2b', 'Major', 'The risk and severity of peripheral neuropathy may be increased during coadministration of telbivudine with alfa interferons. The mechanism of interaction has not been described, although these agents individually have been associated with neuropathy and may have additive effects during concomitant use.', 'DDInter', 1767317477, 'The safety and efficacy of telbivudine in combination with alfa interferons for the treatment of chronic hepatitis B have not been demonstrated, thus concomitant use should generally be avoided. Coadministration of telbivudine with pegylated interferon alfa-2a is specifically contraindicated by the manufacturer. Patients treated with these agents should be advised to report any numbness, tingling, and/or burning sensations in the arms and/or legs, with or without gait disturbance. Telbivudine therapy should be interrupted if peripheral neuropathy is suspected, and discontinued if confirmed.', 'Synergism', 'The safety and efficacy of telbivudine in combination with alfa interferons for the treatment of chronic hepatitis B have not been demonstrated, thus concomitant use should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
