INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253040, 'Homatropine', 'Topiramate', 'Major', 'Certain drugs such as carbonic anhydrase inhibitors and drugs with anticholinergic activity (e.g., antihistamines, antispasmodics, neuroleptics, phenothiazines, skeletal muscle relaxants, tricyclic antidepressants, disopyramide) may potentiate the risk of oligohidrosis and hyperthermia associated occasionally with the use of topiramate, particularly in pediatric patients.  These agents may alter electrolyte and fluid balance (carbonic anhydrase inhibition), inhibit peripheral sweating mechanisms (anticholinergic effect), and/or interfere with core body temperature regulation in the hypothalamus (neuroleptics and phenothiazines), resulting in the inability to adjust to temperature changes, especially in hot weather.  Also, agents with anticholinergic activity frequently cause drowsiness and other central nervous system-depressant effects, which may be additively or synergistically increased in patients also treated with topiramate.', NULL, 'Caution is advised when topiramate is prescribed with other drugs that predispose patients to heat-related disorders, including carbonic anhydrase inhibitors and drugs with anticholinergic activity.', NULL, 'Caution is advised when topiramate is prescribed with other drugs that predispose patients to heat-related disorders, including carbonic anhydrase inhibitors and drugs with anticholinergic activity.  Patients, particularly pediatric patients, should be monitored closely for evidence of decreased sweating and increased body temperature, especially in warm or hot weather.  Proper hydration before and during vigorous activities or exposure to warm temperatures is recommended.  Patients (or their guardians or caregivers) should contact their physician immediately if they are not sweating as usual, with or without a fever.  Ambulatory patients treated with topiramate and agents with anticholinergic activity should also be made aware of the possibility of additive CNS effects (e.g., drowsiness, dizziness, lightheadedness, confusion) and counseled to avoid activities requiring mental alertness until they know how these agents affect them.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/300535/', '[1] "Product Information. Topamax (topiramate)." Ortho McNeil Pharmaceutical  (2001):', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253041, 'Homatropine', 'Tramadol', 'Moderate', 'Coadministration of opioids with anticholinergic agents may result in additive central nervous system (CNS), gastrointestinal, and genitourinary effects.', NULL, 'Caution and close monitoring of central nervous system, gastrointestinal, and genitourinary adverse effects are recommended when opioids are used with anticholinergic agents.', NULL, 'Caution and close monitoring of central nervous system, gastrointestinal, and genitourinary adverse effects are recommended when opioids are used with anticholinergic agents.  Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination until they know how these agents affect them, and to notify their physician if they experience excessive or prolonged CNS effects that interfere with their normal activities.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/300536/', '[1] "Product Information. Demerol (meperidine)." Sanofi Winthrop Pharmaceuticals  (2002):[2] "Product Information. Dolophine (methadone)." Lilly, Eli and Company  (2002):[3] "Product Information. Tylenol with Codeine (acetaminophen-codeine)." Janssen Pharmaceuticals  (2001):[4] "Product Information. Duragesic Transdermal System (fentanyl)." Janssen Pharmaceutica, Titusville, NJ.[5] "Product Information. Ultram (tramadol)." McNeil Pharmaceutical  (2001):[6] "Product Information. OxyContin (oxycodone)." Purdue Frederick Company  (2001):[7] "Product Information. Kadian (morphine)." Astra-Zeneca Pharmaceuticals  (2001):[8] "Product Information. DepoDur (morphine liposomal)." Endo Laboratories LLC  (2004):[9] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[10] "Product Information. Opana (oxymorphone)." Endo Laboratories LLC  (2006):[11] "Product Information. Nucynta (tapentadol)." PriCara Pharmaceuticals  (2009):[12] "Product Information. Exalgo (hydromorphone)." Covidien  (2010):[13] "Product Information. Belbuca (buprenorphine)." Endo Pharmaceuticals Solutions Inc  (2016):[14] "Product Information. Alfentanil Hydrochloride (alfentanil)." Akorn Inc  (2017):[15] "Product Information. SUFentanil Citrate (sufentanil)." Akorn Inc  (2017):[16] "Product Information. Lortab (acetaminophen-hydrocodone)." Akorn Inc  (2017):[17] "Product Information. Levorphanol Tartrate (levorphanol)." Sentynl Therapeutics  (2017):[18] "Product Information. Naloxone HCl-Pentazocine HCl (naloxone-pentazocine)." Actavis U.S. (Amide Pharmaceutical Inc)  (2018):[19] "Product Information. Apadaz (acetaminophen-benzhydrocodone)." KemPharm, Inc  (2018):', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253042, 'Homatropine', 'Tranylcypromine', 'Moderate', 'Agents with anticholinergic properties (e.g., sedating antihistamines; antispasmodics; neuroleptics; phenothiazines; skeletal muscle relaxants; tricyclic antidepressants; disopyramide) may have additive effects when used in combination.  Excessive parasympatholytic effects may result in paralytic ileus, hyperthermia, heat stroke, and the anticholinergic intoxication syndrome.  Peripheral symptoms of intoxication commonly include mydriasis, blurred vision, flushed face, fever, dry skin and mucous membranes, tachycardia, urinary retention, and constipation.  Central symptoms may include memory loss, disorientation, incoherence, hallucinations, psychosis, delirium, hyperactivity, twitching or jerking movements, stereotypy, and seizures.  Central nervous system-depressant effects may also be additively or synergistically increased when these agents are combined, especially in elderly or debilitated patients.  Use of neuroleptics in combination with other neuroleptics or anticholinergic agents may increase the risk of tardive dyskinesia.  In addition, some neuroleptics and tricyclic antidepressants may cause prolongation of the QT interval and theoretically, concurrent use of two or more drugs that can cause QT interval prolongation may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Caution is advised when agents with anticholinergic properties are combined, particularly in the elderly and those with underlying organic brain disease, who tend to be more sensitive to the central anticholinergic effects of these drugs and in whom toxicity symptoms may be easily overlooked.', NULL, 'Caution is advised when agents with anticholinergic properties are combined, particularly in the elderly and those with underlying organic brain disease, who tend to be more sensitive to the central anticholinergic effects of these drugs and in whom toxicity symptoms may be easily overlooked.  Patients should be advised to notify their physician promptly if they experience potential symptoms of anticholinergic intoxication such as abdominal pain, fever, heat intolerance, blurred vision, confusion, and/or hallucinations.  Ambulatory patients should be counseled to avoid activities requiring mental alertness until they know how these agents affect them.  A reduction in anticholinergic dosages may be necessary if excessive adverse effects develop.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/300537/', '[1] Stadnyk AN, Glezos JD "Drug-induced heat stroke." Can Med Assoc J 128 (1983):  957-9[2] Zelman S, Guillan R "Heat stroke in phenothiazine-treated patients: a report of three fatalities." Am J Psychiatry 126 (1970):  1787-90[3] Mann SC, Boger WP "Psychotropic drugs, summer heat and humidity, and hyperplexia: a danger restated." Am J Psychiatry 135 (1978):  1097-100[4] Warnes H, Lehmann HE, Ban TA "Adynamic ileus during psychoactive medication: a report of three fatal and five severe cases." Can Med Assoc J 96 (1967):  1112-3[5] Gershon S, Neubauer H, Sundland DM "Interaction between some anticholinergic agents and phenothiazines." Clin Pharmacol Ther 6 (1965):  749-56[6] Sarnquist F, Larson CP Jr "Drug-induced heat stroke." Anesthesiology 39 (1973):  348-50[7] Johnson AL, Hollister LE, Berger PA "The anticholinergic intoxication syndrome: diagnosis and treatment." J Clin Psychiatry 42 (1981):  313-7[8] Lee BS "Possibility of hyperpyrexia with antipsychotic and anticholinergic drugs." J Clin Psychiatry 47 (1986):  571[9] Forester D "Fatal dr