INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253137, 'Insulin aspart (aspart protamine)', 'Carteolol', 'Moderate', 'Specifically, inhibition of catecholamine-mediated glycogenolysis and glucose mobilization in association with beta-blockade can potentiate insulin-induced hypoglycemia in diabetics and delay the recovery of normal blood glucose levels.', NULL, 'In general, cardioselective beta-blockers are considered safer than noncardioselective agents in the treatment of diabetic patients.', NULL, 'In general, cardioselective beta-blockers are considered safer than noncardioselective agents in the treatment of diabetic patients.  Nevertheless, caution is advised if they are prescribed to patients treated with insulin or oral antidiabetic agents that can cause hypoglycemia (e.g., insulin secretagogues), as cardioselectivity is not absolute and larger doses of beta-1 selective agents may pose some of the same risks as nonselective agents.  Patients should be advised of the need for regular blood glucose monitoring and be aware that certain symptoms of hypoglycemia such as tremor and tachycardia may be masked.  However, other symptoms such as headache, dizziness, drowsiness, confusion, nausea, hunger, weakness, and perspiration may be unaffected.  The same precautions are applicable in diabetic patients treated with ophthalmic beta-blockers.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/300661/', '[1] Shepherd AM, Lin M-S, Keeton TK "Hypoglycemia-induced hypertension in a diabetic patient on metoprolol." Ann Intern Med 94 (1981):  357-8[2] Micossi P, Pollavini G, Raggi U, et al. "Effects of metoprolol and propranolol on glucose tolerance and insulin secretion in diabetes mellitus." Horm Metab Res 16 (1984):  59-63[3] Popp DA, Tse TF, Shah SD, et al. "Oral propranolol and metoprolol both impair glucose recovery from insulin-induced hypoglycemia in insulin-dependent diabetes mellitus." Diabetes Care 7 (1984):  243-7[4] Mann SJ, Krakoff LR "Hypertensive crisis caused by hypoglycemia and propranolol." Arch Intern Med 144 (1984):  2427-8[5] Groop L, Totterman KJ, Harno K, Gordin A "Influence of beta-blocking drugs on glucose metabolism in patients with non-insulin dependent diabetes mellitus." Acta Med Scand 211 (1982):  7-12[6] Viberti GC, Keen H, Bloom SR "Beta blockade and diabetes mellitus: effect of oxprenolol and metoprolol on the metabolic, cardiovascular, and hormonal response to insulin-induced hypoglycemia in insulin-dependent diabetics." Metabolism 29 (1980):  873-9[7] Viberti GC, Keen H, Bloom SR "Beta blockade and diabetes mellitus: effect of oxprenolol and metoprolol on the metabolic, cardiovascular, and hormonal response to insulin-induced hypoglycemia in normal subjects." Metabolism 29 (1980):  866-72[8] Newman RJ "Comparison of propranolol, metoprolol, and acebutolol on insulin-induced hypoglycaemia." Br Med J 2 (1976):  447-9[9] Smith U "Beta blockade in diabetes." N Engl J Med 299 (1978):  1467[10] Zaman R, Kendall MJ, Biggs PI "The effect of acebutolol and propranolol on the hypoglycaemic action of glibenclamide." Br J Clin Pharmacol 13 (1982):  507-12[11] Munroe WP, Rindone JP, Kershner RM "Systemic side effects associated with the ophthalmic administratiion of timolol." Drug Intell Clin Pharm 19 (1985):  85-9[12] Ostman J "B-adrenergic blockade and diabetes mellitus." Acta Med Scand 672 (1983):  69-77[13] Deacon SP, Karunanayake A, Barnett D "Acebutolol, atenolol, and propranolol and metabolic responses to acute hypoglycaemia in diabetes." Br Med J 12 (1977):  1255-7[14] Pollare T, Lithell H, Selinus I, Berne C "Sensitivity to insulin during treatment with atenolol and metoprolol: a randomised, double blind study of effects on carbohydrate and lipoprotein metabolism in hypertensive patients." BMJ 298 (1989):  1152-7[15] Sinclair AJ, Davies IB, Warrington SJ "Betaxolol and glucose-insulin relationships: studies in normal subjects taking glibenclamide or metformin." Br J Clin Pharmacol 30 (1990):  699-702[16] "New Zealand Committee on Adverse Drug Reactions. Ninth Annual Report." N Z Dent J 71 (1975):  28-32', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253138, 'Insulin aspart (aspart protamine)', 'Deserpidine', 'Moderate', 'Postganglionic adrenergic blocking agents may potentiate the hypoglycemic effects of insulin and insulin secretagogues (e.g., sulfonylureas, meglitinides).  Limited data have demonstrated possible hypoglycemic activity with agents such as guanethidine and reserpine.  The mechanism may involve depletion of catecholamine stores from adrenergic nerve endings, which can interfere with glycogenolysis and other mechanisms related to regulation of blood glucose levels.', NULL, 'Close monitoring for the development of hypoglycemia is recommended when postganglionic adrenergic blocking agents are coadministered with insulin or insulin secretagogues, particularly in patients with advanced age and/or renal impairment.', NULL, 'Close monitoring for the development of hypoglycemia is recommended when postganglionic adrenergic blocking agents are coadministered with insulin or insulin secretagogues, particularly in patients with advanced age and/or renal impairment.  Insulin and oral antidiabetic dosages may require adjustment if an interaction is suspected.  Patients should be instructed about the need for regular monitoring of blood glucose levels and be aware that certain symptoms of hypoglycemia such as tremor and tachycardia may be masked during antiadrenergic therapy.  However, other symptoms such as headache, dizziness, drowsiness, nausea, hunger, and sweating may be unaffected.  Patients should be observed for loss of glycemic control when antiadrenergic therapy is withdrawn.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/300665/', '[1] Gupta KK "Guanethidine and diabetes." Br Med J 2 (1968):  697-8[2] Gupta KK "The antidiabetic action of guanethidine." Postgrad Med J 45 (1969):  455-6[3] Gupta KK "Guanethidine and glucose tolerance in diabetics." Br Med J 3 (1968):  679', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253139, 'Insulin aspart (aspart protamine)', 'Dexamethasone (topical)', 'Minor', 'The efficacy of insulin and other antidiabetic agents may be diminished by topical corticosteroids, particularly during prolonged or indiscriminate use.  Corticosteroids can raise blood glucose level by antagonizing the action and suppressing the secretion of insulin, which results in inhibition of peripheral glucose uptake and increased gluconeogenesis.', NULL, 'Systemic absorption of topical corticosteroids may occur depending on the vehicle and concentration of the preparation, the size of the application area, the integrity of the skin, and the duration of administration.', NULL, 'Systemic absorption of topical corticosteroids may occur depending on the vehicle and concentration of the preparation, the size of the application area, the integrity of the skin, and the duration of administration.  Use of occlusive dressings over the applied areas may also increase percutaneous absorption.  Given equivalent doses, small children are generally at the greatest risk because of their larger skin surface to body mass ratios.  If possible, the use of highly potent agents (e.g., augmented betamethasone, clobetasol, diflorasone, and halobetasol) should be avoided in children and limited to small areas for 2 weeks in adults.  Close clinical monitoring of glycemic control is recommended if topical corticosteroids are administered chronically and/or to large areas in diabetic patients.', 'Antagonism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/300666/', '[1] Seale JP, Compton MR "Side-effects of corticosteroid agents." Med J Aust 144 (1986):  139-42[2] Ludvik B, Clodi M, Kautzky-Willer A, Capek M, Hartter E, Pacini G, Prager R "Effect of dexamethasone on insulin sensitivity, islet amyloid polypeptide and insulin secretion in humans." Diabetologia 36 (1993):  84-7[3] Carruthers JA, Staughton RC, August PJ "Penetration of topical steroid preparations." Arch Dermatol 113 (1977):  522[4] Pace WE "Topical corticosteroids." Can Med Assoc J 108 (1973):  11 passim[5] "Product Information. Ultravate (halobetasol topical)." Apothecon Inc  (2022):[6] "Product Information. Diprolene (betamethasone topical)." Schering Corporation  (2001):[7] "Product Information. Temovate (clobetasol topical)." Glaxo Wellcome[8] "Product Information. Psorcon (diflorasone topical)." Rhone Poulenc Rorer  (2001):', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253140, 'Insulin aspart (aspart protamine)', 'Dexfenfluramine', 'Moderate', 'Since weight loss may affect glycemic control and increase the risk of hypoglycemia in type 2 diabetes mellitus, patients receiving anorectic drugs may require periodic adjustments of their antidiabetic medications.', NULL, 'Blood glucose should be closely monitored during weight loss treatment, and appropriate changes made to the antidiabetic drug regimen as needed.', NULL, 'Blood glucose should be closely monitored during weight loss treatment, and appropriate changes made to the antidiabetic drug regimen as needed.  Patients should be apprised of the risk of hypoglycemia and be alert to potential signs and symptoms such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia.  For antidiabetic medications that are not glucose-dependent, reduction in the dosage should be considered to mitigate the risk of hypoglycemia.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/300667/', '[1] "Product Information. Xenical (orlistat)." Roche Laboratories  (2001):[2] "Product Information. Didrex (benzphetamine)." Pharmacia and Upjohn  (2001):[3] "Product Information. Suprenza (phentermine)." Akrimax Pharmaceuticals  (2012):[4] "Product Information. Belviq (lorcaserin)." Eisai Inc  (2012):', 1767365211);
