INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253173, 'Insulin aspart (aspart protamine)', 'Troglitazone', 'Moderate', 'Coadministration of a thiazolidinedione in combination with insulin may increase the risk of edema compared to insulin alone.  The mechanism is unknown but may involve enhancement of the antinatriuretic and/or peripheral vasodilatory effects of insulin.', NULL, 'Caution is advised during coadministration of thiazolidinedione and insulin therapy.', NULL, 'Caution is advised during coadministration of thiazolidinedione and insulin therapy.  Patients at risk for heart failure should be closely monitored.  Patients should be advised to notify their physician immediately if they experience signs and symptoms of heart failure such as fluid retention, edema, rapid weight gain, or shortness of breath.  Patients should also be apprised of the increased risk of hypoglycemia and be alert to potential signs and symptoms of hypoglycemia such as headache, dizziness, drowsiness, nausea, hunger, tremor, weakness, sweating, palpitations.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/300708/', '[1] Raskin P, Rendell M, Riddle MC, Dole JF, Freed MI, Rosenstock J "A randomized trial of rosiglitazone therapy in patients with inadequately controlled insulin-treated type 2 diabetes." Diabetes Care 24 (2001):  1226-32[2] King KA, Levi VE "Prevalence of edema in patients receiving combination therapy with insulin and thiazolidinedione." Am J Health Syst Pharm 61 (2004):  390-3[3] Rosenstock J, Einhorn D, Hershon K, Glazer NB "Efficacy and safety of pioglitazone in type 2 diabetes: a randomised, placebo-controlled study in patients receiving stable insulin therapy." Int J Clin Pract 56 (2002):  251-7', 1767365211);
