INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253206, 'Isometheptene', 'Duloxetine', 'Moderate', 'Additive or synergistic effects on blood pressure and heart rate may occur when serotonin-norepinephrine reuptake inhibitors (SNRIs) are combined with sympathomimetic agents such as nasal decongestants, adrenergic bronchodilators, ophthalmic vasoconstrictors, and systemic vasopressors.', NULL, 'Caution is advised if SNRIs are used with other drugs that can increase blood pressure and/or heart rate.', NULL, 'Caution is advised if SNRIs are used with other drugs that can increase blood pressure and/or heart rate.  Blood pressure and pulse should be measured prior to initiating SNRI therapy and monitored at regular intervals thereafter.  Dose reduction or discontinuation of the SNRI should be considered in patients who experience a sustained increase in blood pressure or pulse rate.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/300748/', '[1] "Product Information. Effexor (venlafaxine)." Wyeth-Ayerst Laboratories  (2001):[2] "Product Information. Cymbalta (duloxetine)." Lilly, Eli and Company  (2004):[3] "Product Information. Pristiq (desvenlafaxine)." Wyeth Laboratories  (2008):[4] "Product Information. Savella (milnacipran)." Forest Pharmaceuticals  (2009):[5] "Product Information. Nucynta (tapentadol)." PriCara Pharmaceuticals  (2009):[6] "Product Information. Fetzima (levomilnacipran)." Forest Pharmaceuticals  (2013):', 1767365211);
