INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253256, 'Dichloralphenazone', 'Aldesleukin', 'Moderate', 'Sedatives, tranquilizers, muscle relaxants, antidepressants, and other central nervous system (CNS) depressants may have additive CNS-depressant effects with aldesleukin.', NULL, 'Caution is advised if aldesleukin is used with sedatives, hypnotics, tranquilizers, or other CNS-active medications.', NULL, 'Caution is advised if aldesleukin is used with sedatives, hypnotics, tranquilizers, or other CNS-active medications.  Patients should be monitored for CNS depression and other CNS adverse effects.  Treatment with aldesleukin should be discontinued if severe lethargy or somnolence develop.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/300804/', '[1] "Product Information. Proleukin (aldesleukin)." Chiron Therapeutics  (2001):[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Agencia Espala de Medicamentos y Productos Sanitarios Healthcare "Centro de informaci online de medicamentos de la AEMPS - CIMA.  https://cima.aemps.es/cima/publico/home.html"   (2008):', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253257, 'Dichloralphenazone', 'Alfentanil', 'Major', 'Concomitant use of opioids with benzodiazepines or other central nervous system (CNS) depressants (e.g., nonbenzodiazepine sedatives/hypnotics, anxiolytics, muscle relaxants, general anesthetics, antipsychotics, other opioids, alcohol) may result in profound sedation, respiratory depression, coma, and death.  The risk of hypotension may also be increased with some CNS depressants (e.g., alcohol, benzodiazepines, phenothiazines).', NULL, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate.', NULL, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate.  If coadministration is necessary, the dosage and duration of each drug should be limited to the minimum required to achieve desired clinical effect, with cautious titration and dosage adjustments when needed.  Patients should be monitored closely for signs and symptoms of respiratory depression and sedation, and advised to avoid driving or operating hazardous machinery until they know how these medications affect them.  Cough medications containing opioids (e.g., codeine, hydrocodone) should not be prescribed to patients using benzodiazepines or other CNS depressants including alcohol.  For patients who have been receiving extended therapy with both an opioid and a benzodiazepine and require discontinuation of either medication, a gradual tapering of dose is advised, since abrupt withdrawal may lead to withdrawal symptoms.  Severe cases of benzodiazepine withdrawal, primarily in patients who have received excessive doses over a prolonged period, may result in numbness and tingling of extremities, hypersensitivity to light and noise, hallucinations, and epileptic seizures.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/300805/', '[1] US Food and Drug Administration "FDA warns about serious risks and death when combining opioid pain or cough medicines with benzodiazepines; requires its strongest warning.  http://www.fda.gov/downloads/Drugs/DrugSafety/UCM518672.pdf"   (2016):', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253258, 'Dichloralphenazone', 'Alfuzosin', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation.  Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', NULL, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', NULL, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.  Some authorities recommend avoiding alcohol in patients receiving vasodilating antihypertensive drugs.  Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/300806/', '[1] Sternbach H "Fluoxetine-associated potentiation of calcium-channel blockers." J Clin Psychopharmacol 11 (1991):  390-1[2] Feder R "Bradycardia and syncope induced by fluoxetine." J Clin Psychiatry 52 (1991):  139[3] Ellison JM, Milofsky JE, Ely E "Fluoxetine-induced bradycardia and syncope in two patients." J Clin Psychiatry 51 (1990):  385-6[4] Rodriguez de la Torre B, Dreher J, Malevany I, et al. "Serum levels and cardiovascular effects of tricyclic antidepressants and selective serotonin reuptake inhibit