INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253480, 'Brentuximab vedotin', 'Rifampicin', 'Moderate', 'Coadministration with rifampin may decrease the plasma concentrations of monomethyl auristatin E (MMAE), the microtubule-disrupting component of brentuximab vedotin.  MMAE is primarily metabolized by CYP450 3A4.', NULL, 'The potential for diminished efficacy of brentuximab vedotin and increased risk of hepatic injury should be considered when brentuximab vedotin is used with rifampin.', NULL, 'The potential for diminished efficacy of brentuximab vedotin and increased risk of hepatic injury should be considered when brentuximab vedotin is used with rifampin.  Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.  Liver enzymes and bilirubin should be measured before and during treatment, especially in patients with underlying hepatic disease or marked baseline transaminase elevations.  Patients experiencing new, worsening, or recurrent hepatotoxicity may require a delay, change in dosage, or discontinuation of brentuximab vedotin in accordance with the product labeling.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301038/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports.  http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid"   (2007):[3] "Product Information. Adcetris (brentuximab vedotin)." Seattle Genetics Inc  (2011):', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253481, 'Isoniazid', 'Brentuximab vedotin', 'Moderate', 'Brentuximab vedotin can cause peripheral neuropathy, and concurrent use of other agents that are also associated with this adverse effect can potentiate the risk.', NULL, 'The potential for increased risk and/or severity of peripheral neuropathy and hepatotoxicity should be considered when brentuximab vedotin is used with other drugs that are also associated with these adverse effects.', NULL, 'The potential for increased risk and/or severity of peripheral neuropathy and hepatotoxicity should be considered when brentuximab vedotin is used with other drugs that are also associated with these adverse effects.  Patients should be closely monitored for symptoms of neuropathy such as burning, tingling, pain, numbness, or weakness.  Liver enzymes and bilirubin should be measured before and during treatment, especially in patients with underlying hepatic disease or marked baseline transaminase elevations.  Patients experiencing new, worsening, or recurrent neuropathy or hepatotoxicity may require a delay, change in dosage, or discontinuation of brentuximab vedotin in accordance with the product labeling.  Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301039/', '[1] "Product Information. Adcetris (brentuximab vedotin)." Seattle Genetics Inc  (2011):', 1767365211);
