INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253496, 'Aripiprazole', 'Lumacaftor', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of aripiprazole and its active metabolite, dehydro-aripiprazole.', NULL, 'When aripiprazole is administered orally, the prescribing information recommends doubling the usual dosage over 1 to 2 weeks following the addition of a potent CYP450 3A4 inducer.', NULL, 'When aripiprazole is administered orally, the prescribing information recommends doubling the usual dosage over 1 to 2 weeks following the addition of a potent CYP450 3A4 inducer.  Additional dosage adjustments should be based on clinical evaluation.  Upon discontinuation of the inducer, aripiprazole dosage should be reduced to the original level over 1 to 2 weeks.  When given as an immediate-acting intramuscular injection, the aripiprazole dosage should also be doubled in patients receiving potent CYP450 3A4 inducers.  For patients receiving extended-release intramuscular formulations of aripiprazole, concomitant use of potent CYP450 3A4 inducers should generally be avoided for greater than 14 days, although some formulations may be administered, with or without a dosage adjustment depending on the dose.  The prescribing information for individual aripiprazole products should be consulted for specific recommendations regarding concomitant use with potent CYP450 3A4 inducers.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301061/', '[1] "Product Information. Abilify (ARIPiprazole)." Bristol-Myers Squibb  (2022):[2] "Product Information. Abilify Maintena (ARIPiprazole)." Otsuka A