INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1475494, 'Vibrio cholerae CVD 103-HgR strain live antigen', 'Pemetrexed', 'Moderate', 'The administration of inactivated, killed, or otherwise noninfectious vaccines to immunosuppressed patients is generally safe but may be associated with a diminished or suboptimal immunologic response due to antibody inhibition. Such patients may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', NULL, 'In general, the US Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients.', NULL, 'In general, the US Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients. For Haemophilus influenzae b vaccine, some experts recommend that it be administered at least 2 weeks before starting or 3 months after discontinuing chemotherapy when used in patients with Hodgkin''s disease. For rabies vaccine, some authorities suggest that immunosuppressive agents should generally be avoided during postexposure therapy except when absolutely necessary for the treatment of other conditions.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1475495, 'Choline salicylate', 'Pemetrexed', 'Moderate', 'INTERVAL: Coadministration with nonsteroidal anti-inflammatory drugs (NSAIDs) may increase the plasma concentrations of pemetrexed. The mechanism has not been described but may be related to NSAID inhibition of renal prostaglandins. Use of NSAIDs has been associated with nephropathy manifested as elevations in serum creatinine and BUN, tubular necrosis, glomerulitis, renal papillary necrosis, acute interstitial nephritis, nephrotic syndrome, and renal failure. Since pemetrexed is primarily eliminated unchanged by renal excretion, coadministration with NSAIDs may result in delayed and/or decreased clearance.', NULL, 'Ibuprofen at 400 mg and aspirin at 325 mg four times a day, or less, may be used with pemetrexed in patients with normal renal function.', NULL, 'Ibuprofen at 400 mg and aspirin at 325 mg four times a day, or less, may be used with pemetrexed in patients with normal renal function. However, caution is advised in patients with mild to moderate renal insufficiency (creatinine clearance 45 to 79 mL/min). These patients should avoid taking NSAIDs with short elimination half-lives (e.g., diclofenac, etodolac, fenoprofen, flurbiprofen, ibuprofen, indomethacin, ketoprofen, ketorolac, meclofenamate, mefenamic acid, sulindac, tolmetin, low dosages of salicylates) 2 days before to 2 days after pemetrexed administration. If concomitant administration is necessary, patients should be monitored closely for toxicity, especially myelosuppression, nephrotoxicity, and gastrointestinal toxicity.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
