        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1475709, 'Probenecid', 'Penicillamine', 'Moderate', 'Probenecid may interfere with penicillamine metabolite elimination. The therapeutic effect of penicillamine may be reduced and increased toxicity may occur. In one study with two cystinuric patients given single doses of probenecid and penicillamine, increased cysteine excretion and decreased excretion of penicillamine metabolites occurred.', NULL, 'Concomitant use should be avoided in cystinuric patients.', NULL, 'Concomitant use should be avoided in cystinuric patients.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
