    ) VALUES (1475710, 'Quinapril', 'Penicillamine', 'Moderate', 'INTERVAL: Administration of quinapril with some oral medications may significantly decrease their absorption and may result in subtherapeutic serum concentrations. The proposed mechanism is chelation of the drug by the magnesium hydroxide excipient in quinapril tablets.', NULL, 'The manufacturer does not make a specific recommendation, but generally the administration of these drugs and magnesium-containing products should be separated by at least two to three hours.', NULL, 'The manufacturer does not make a specific recommendation, but generally the administration of these drugs and magnesium-containing products should be separated by at least two to three hours.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
