    ) VALUES (1475779, 'Fospropofol', 'Pentobarbital', 'Moderate', 'Additive central nervous system and cardiorespiratory depressant effects may occur when fospropofol or propofol is administered with other depressants such as sedative-hypnotic agents and narcotic analgesics.', NULL, 'Patients should be monitored closely for excessive sedation and cardiorespiratory depression, and the medication dosage(s) adjusted accordingly.', NULL, 'Patients should be monitored closely for excessive sedation and cardiorespiratory depression, and the medication dosage(s) adjusted accordingly. Supportive therapy should be provided if needed.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
