INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253506, 'Bexarotene', 'Lumacaftor', 'Minor', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of bexarotene.  In vitro studies suggest that bexarotene is metabolized by CYP450 3A4.  However, concomitant administration with multiple doses of ketoconazole, a potent CYP450 3A4 inhibitor, did not alter bexarotene plasma concentrations, which would imply that bexarotene elimination is not substantially dependent on CYP450 3A4 metabolism in vivo.', NULL, 'Although studies may now indicate that CYP450 3A4 inducers or inhibitors do not have a significant effect on bexarotene metabolism, we still conservatively recommend avoiding the concomitant use of strong CYP450 3A4 inducers (e.', NULL, 'Although studies may now indicate that CYP450 3A4 inducers or inhibitors do not have a significant effect on bexarotene metabolism, we still conservatively recommend avoiding the concomitant use of strong CYP450 3A4 inducers (e.g., carbamazepine, phenytoin, phenobarbital, rifampin) when taking bexarotene.', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301076/', '[1] "Product Information. Targretin (bexarotene)." Ligand Pharmaceuticals  (2001):', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253507, 'Binimetinib', 'Lumacaftor', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp), uridine diphosphate glucuronosyltransferase (UGT) 1A1 or 2B7, and/or breast cancer resistance protein (BCRP) may increase the plasma levels and risk of adverse effects of binimetinib.  The proposed mechanism involves the reduced metabolic clearance of binimetinib through inhibition of P-gp, UGT 1A1, UGT 2B7, and/or BCRP.', NULL, 'Until further information is available, caution is recommended if binimetinib must be used concomitantly with P-gp, UGT 1A1, UGT 2B7, and/or BCRP inhibitors.', NULL, 'Until further information is available, caution is recommended if binimetinib must be used concomitantly with P-gp, UGT 1A1, UGT 2B7, and/or BCRP inhibitors.  Binimetinib should be monitored more closely whenever a P-gp, UGT 1A1, UGT 2B7, and/or BCRP inhibitor is added to or withdrawn from therapy, and the binimetinib dosage adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301077/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Mektovi (binimetinib)." Array BioPharma Inc.  (2018):', 1767365211);
