INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253562, 'Doxorubicin', 'Lumacaftor', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of doxorubicin, which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of doxorubicin with CYP450 3A4 inducers should generally be avoided, particularly potent ones like carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St.', NULL, 'Concomitant use of doxorubicin with CYP450 3A4 inducers should generally be avoided, particularly potent ones like carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St. John''s wort.  Close monitoring for potentially reduced efficacy of doxorubicin is recommended if coadministration is required.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301141/', '[1] Riggs CE Jr, Engel S, Wesley M, Wiernik PH, Bachur NR "Doxorubicin pharmacokinetics: prochlorperazine and barbiturate effects." Clin Pharmacol Ther 31 (1982):  263[2] "Product Information. Adriamycin PFS (doxorubicin)." Pharmacia and Upjohn  (2001):[3] "Product Information. Doxil (doxorubicin liposomal)." Sequus Pharmaceuticals Inc  (2001):[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253563, 'Dronedarone', 'Lumacaftor', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations and pharmacologic effects of dronedarone, which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of dronedarone with potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St.', NULL, 'Concomitant use of dronedarone with potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St. John''s wort should generally be avoided.  The extent to which other, less potent CYP450 3A4 inducers may interact with dronedarone is unknown.  Caution is advised if they are used with dronedarone.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301142/', '[1] "Product Information. Multaq (dronedarone)." sanofi-aventis  (2009):', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253564, 'Droperidol', 'Lumacaftor', 'Moderate', 'Theoretically, potent CYP450 3A4 inducers may decrease serum concentrations of butyrophenones such as benperidol or droperidol.  This interaction has been reported for haloperidol.', NULL, 'The potential for diminished neuroleptic effects of droperidol or benperidol should be considered during coadministration with potent CYP450 3A4 inducers.', NULL, 'The potential for diminished neuroleptic effects of droperidol or benperidol should be considered during coadministration with potent CYP450 3A4 inducers.  Pharmacologic response to these medications should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the droperidol or benperidol dosage adjusted as necessary.  Patients taking potent CYP450 3A4 inducers that are anticonvulsants should be monitored for loss of seizure control.  Additionally, when droperidol or benperidol are used in combination with other drugs that cause CNS and/or respiratory depression, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.  Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination until they know how these agents affect them, and to notify their doctor if they experience seizures or excessive CNS effects that interfere with their normal activities.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301143/', '[1] Kidron R, Averbuch I, Klein E, Belmaker RH "Carbamazepine-induced reduction of blood levels of haloperidol in chronic schizophrenia." Biol Psychiatry 20 (1985):  219-22[2] Takeda M, Nishinuma K, Yamashita S, et al. "Serum haloperidol levels of schizophrenics receiving treatment for tuberculosis." Clin Neuropharmacol 9 (1986):  386-97[3] "Product Information. Haldol (haloperidol)." McNeil Pharmaceutical  (2002):[4] "Product Information. Rifadin (rifampin)." Hoechst Marion Roussel  (2001):[5] Arana GW, Goff DC, Friedman H, et al. "Does carbamazepine-induced reduction of plasma haloperidol levels worsen psychotic symptoms?" Am J Psychiatry 143 (1986):  650-1[6] Jann MW, Ereshefsky L, Saklad SR, et al. "Effects of carbamazepine on plasma halopeidol levels." J Clin Pharmacol 5 (1985):  106-9[7] Kahn EM, Schulz SC, Perel JM, Alexander JE "Change in haloperidol level due to carbamazepine--a complicating factor in combined medication for schizophrenia." J Clin Psychopharmacol 10 (1990):  54-7[8] Kim YH, Cha IJ, Shim JC, Shin JG, Yoon YR, Kim YK, Kim JI, Park GH, Jang IJ, Woo JI, Shin SG "Effect of rifampin on the plasma concentration and the clinical effect of haloperidol concomitantly administered to schizophrenic patients." J Clin Psychopharmacol 16 (1996):  247-52[9] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[10] Cerner Multum, Inc. "Australian Product Information." O 0[11] Agencia Espaola de Medicamentos y Productos Sanitarios Healthcare "Centro de informacin online de medicamentos de la AEMPS - CIMA.  https://cima.aemps.es/cima/publico/home.html"   (2008):', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253565, 'Drospirenone', 'Lumacaftor', 'Major', 'Coadministration with lumacaftor may significantly decrease the plasma concentrations of contraceptive hormones.  The proposed mechanism is lumacaftor induction of the CYP450 3A4-mediated metabolism of estrogens and progestins.', NULL, 'Hormonal contraceptives, including oral, injectable, transdermal, and implantable forms, should not be relied upon as an effective method of contraception when coadministered with lumacaftor/ivacaftor.', NULL, 'Hormonal contraceptives, including oral, injectable, transdermal, and implantable forms, should not be relied upon as an effective method of contraception when coadministered with lumacaftor/ivacaftor.  Female patients of childbearing potential should use an alternative or additional method of contraception.  Intrauterine systems are unlikely to be significantly affected because of their local action.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301144/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Orkambi (ivacaftor-lumacaftor)." Vertex Pharmaceuticals  (2015):[3] Faculty of Sexual & Reproductive Healthcare "FSRH Clinical Guidance: Drug Interactions with Hormonal Contraception.  file:///C:/Users/df033684/Downloads/ceuguidancedruginteractionshormonal.pdf"   (2016):', 1767365211);
