INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253567, 'Efavirenz', 'Lumacaftor', 'Moderate', 'Coadministration with lumacaftor may decrease the plasma concentrations and therapeutic efficacy of drugs that are substrates of CYP450 3A4, 2B6, 2C19, 2C8, and/or 2C9.  Lumacaftor is a potent inducer of CYP450 3A4 in vivo.', NULL, 'Caution is advised when lumacaftor/ivacaftor is prescribed with drugs that undergo metabolism by CYP450 3A4, 2B6, 2C19, 2C8 and/or 2C9, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when lumacaftor/ivacaftor is prescribed with drugs that undergo metabolism by CYP450 3A4, 2B6, 2C19, 2C8 and/or 2C9, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lumacaftor/ivacaftor is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301146/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Orkambi (ivacaftor-lumacaftor)." Vertex Pharmaceuticals  (2015):[4] Cerner Multum, Inc. "Canadian Product Information." O 0 (2015):', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253568, 'Elagolix', 'Lumacaftor', 'Moderate', 'Coadministration with inducers of CYP450 3A4 and/or P-glycoprotein (P-gp) may decrease the plasma concentrations of elagolix, which is a substrate of both the metabolic isoenzyme and efflux transporter.', NULL, 'The potential for diminished pharmacologic effects of elagolix should be considered during coadministration with CYP450 3A4 and/or P-gp inducers.', NULL, 'The potential for diminished pharmacologic effects of elagolix should be considered during coadministration with CYP450 3A4 and/or P-gp inducers.  Alternative treatments or a dose adjustment of elagolix may be required if an interaction is suspected.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301148/', '[1] "Product Information. Orilissa (elagolix)." AbbVie US LLC  (2018):', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253569, 'Eliglustat', 'Lumacaftor', 'Major', 'Coadministration with potent inducers of CYP450 3A4 and/or P-glycoprotein (P-gp) may significantly decrease the plasma concentrations of eliglustat, which is primarily metabolized by CYP450 2D6 and, to a lesser extent, CYP450 3A4.  Eliglustat is also a substrate of P-gp efflux transporter.', NULL, 'Concomitant use of eliglustat with potent CYP450 3A4 or P-gp inducers should generally be avoided due to the potential for significantly reduced efficacy.', NULL, 'Concomitant use of eliglustat with potent CYP450 3A4 or P-gp inducers should generally be avoided due to the potential for significantly reduced efficacy.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301149/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Cerdelga (eliglustat)." Genzyme Corporation  (2014):', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253570, 'Elvitegravir', 'Lumacaftor', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of elvitegravir, which is primarily metabolized by the isoenzyme.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic drug levels, concomitant use of elvitegravir with potent CYP450 3A4 inducers is generally not recommended.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic drug levels, concomitant use of elvitegravir with potent CYP450 3A4 inducers is generally not recommended.  In addition, other antiretroviral agents that are typically combined with elvitegravir may be contraindicated for use with potent CYP450 3A4 inducers.  Please refer to the manufacturer''s labeling for the specific elvitegravir-containing product for more information.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301150/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Stribild (cobicistat/elvitegravir/emtricitabine/tenofov)." Gilead Sciences  (2012):[4] "Product Information. Vitekta (elvitegravir)." Gilead Sciences  (2014):[5] "Product Information. Tybost (cobicistat)." Gilead Sciences  (2014):[6] "Product Information. Genvoya (cobicistat/elvitegravir/emtricitabine/tenofov)." Gilead Sciences  (2015):', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253571, 'Enasidenib', 'Lumacaftor', 'Moderate', 'Based on in vitro data, coadministration with enasidenib may increase the plasma concentrations of drugs that are substrates of the transporters, organic anion transporting polypeptides (OATP) 1B1/1B3 and breast cancer resistance protein (BCRP).  The proposed mechanism is decreased clearance due to enasidenib -mediated inhibition of OATP1B1/1B3 and BCRP transporters.', NULL, 'Caution is advised when enasidenib is used concurrently with medications that are substrates of OATP 1B1/1B3 and/or BCRP, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when enasidenib is used concurrently with medications that are substrates of OATP 1B1/1B3 and/or BCRP, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever enasidenib is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301151/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Idhifa (enasidenib)." Celgene Corporation  (2017):', 1767365211);
