INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253573, 'Enfortumab vedotin', 'Lumacaftor', 'Minor', 'Coadministration with potent inducers of CYP450 3A4 may decrease the plasma concentrations of unconjugated monomethyl auristatin E (MMAE), the anti-mitotic and cytotoxic component of enfortumab vedotin.  Enfortumab vedotin is an antibody-drug conjugate (ADC) that releases MMAE via proteolytic cleavage, and MMAE has been shown in vitro to be primarily metabolized by CYP450 3A4.', NULL, 'However, the interaction is not expected to be clinically relevant, since the intact ADC containing the antibody component is required to bind to Nectin-4, an adhesion protein found on the surface of cells, which allows for internalization and cleavage by lysosomal proteases to enable intracellular delivery of MMAE.', NULL, 'However, the interaction is not expected to be clinically relevant, since the intact ADC containing the antibody component is required to bind to Nectin-4, an adhesion protein found on the surface of cells, which allows for internalization and cleavage by lysosomal proteases to enable intracellular delivery of MMAE..', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301153/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Adcetris (brentuximab vedotin)." Seattle Genetics Inc  (2011):[3] Han TH, Gopal AK, Ramchandren R, et al. "CYP3A-mediated drug-drug interaction potential and excretion of brentuximab vedotin, an antibody-drug conjugate, in patients with CD30-positive hematologic malignancies." J Clin Pharmacol 53 (2013):  866-77[4] "Product Information. Padcev (enfortumab vedotin)." Sea