INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253575, 'Enzalutamide', 'Lumacaftor', 'Moderate', 'Coadministration with inducers of CYP450 2C8 and/or 3A4 may decrease the plasma concentrations of enzalutamide, which is primarily metabolized by CYP450 2C8 to its pharmacologically active metabolite, N-desmethyl enzalutamide, and to a lesser extent by CYP450 3A4.', NULL, 'Concomitant use of enzalutamide with potent CYP450 2C8 and/or 3A4 inducers such as carbamazepine, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St.', NULL, 'Concomitant use of enzalutamide with potent CYP450 2C8 and/or 3A4 inducers such as carbamazepine, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St. John''s wort should generally be avoided.  Moderate inducers such as bosentan, efavirenz, etravirine, modafinil, nafcillin, and nevirapine should also be avoided if possible.  The extent to which other, less potent CYP450 3A4 inducers may interact with enzalutamide is unknown.  Caution is advised if they are used with enzalutamide.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301155/', '[1] "Product Information. Xtandi (enzalutamide)." Astellas Pharma US, Inc  (2012):', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253576, 'Eplerenone', 'Lumacaftor', 'Minor', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of eplerenone, which is metabolized by the isoenzyme.  According to the product labeling, administration of a single 100 mg dose of eplerenone with the potent CYP450 3A4 inducer St John''s wort resulted in an approximately 30% decrease in eplerenone systemic exposure (AUC).', NULL, 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of eplerenone, which is metabolized by the isoenzyme.', NULL, 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of eplerenone, which is metabolized by the isoenzyme.  According to the product labeling, administration of a single 100 mg dose of eplerenone with the potent CYP450 3A4 inducer St John''s wort resulted in an approximately 30% decrease in eplerenone systemic exposure (AUC).  This is not considered clinically significant.', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301156/', '[1] "Multum Information Services, Inc. Expert Review Panel"[2] "Product Information. Casodex (bicalutamide)." Astra-Zeneca Pharmaceuticals  (2001):[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Agencia Espala de Medicamentos y Productos Sanitarios Healthcare "Centro de informaci online de medicamentos de la AEMPS - CIMA.  https://cima.aemps.es/cima/publico/home.html"   (2008):', 1767365211);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253577, 'Eravacycline', 'Lumacaftor', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may decrease the plasma concentrations of eravacycline, which undergoes oxidation by CYP450 3A4 and flavin monoxygenase (FMO).', NULL, 'Eravacycline dosage should be increased to 1.', NULL, 'Eravacycline dosage should be increased to 1.5 mg/kg every 12 hours when used with potent CYP450 3A4 inducers.  No dosage adjustment is warranted when eravacycline is used with a weak or moderate inducer.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301157/', '[1] "Product Information. Xerava (eravacycline)." Tetraphase Pharmaceuticals, Inc  (2018):', 1767365211);
