INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253581, 'Erlotinib', 'Lumacaftor', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of erlotinib, which has been shown in vitro to be primarily metabolized by CYP450 3A4 (approximately 80% to 95%) and to a lesser extent by CYP450 1A2.', NULL, 'Concomitant use of erlotinib with potent CYP450 3A4 inducers should generally be avoided.', NULL, 'Concomitant use of erlotinib with potent CYP450 3A4 inducers should generally be avoided.  If coadministration is required, the manufacturer recommends increasing the dosage of erlotinib by 50 mg increments at 2-week intervals up to a maximum of 450 mg as tolerated.  Close clinical and laboratory monitoring for altered efficacy and safety, including renal and liver functions and serum electrolytes, is recommended.  Patients who receive an increased dose of erlotinib will need a dosage reduction upon discontinuation of the inducer.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301161/', '[1] Li J, Zhao M, He P, Hidalgo M, Baker SD "Differential metabolism of gefitinib and erlotinib by human cytochrome p450 enzymes." Clin Cancer Res 13 (2007):  3731-7[2] "Product Information. Tarceva (erlotinib)." Genentech  (2018):[3] "Product Information. Tarceva (erlotinib)." Hoffmann-La Roche Limited  (2018):[4] "Product Information. Tarceva (erlotinib)." Roche Products Ltd  (2022):[5] "Product Information. Tarceva (erlotinib)." Roche Products Pty Ltd  (2022):[6] Hamilton M, Wolf JL, Drolet DW, et al. "The effect of rifampicin, a prototypical CYP3A4 inducer, on erlotinib pharmacokinetics in healthy 