INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (253582, 'Erythromycin', 'Lumacaftor', 'Major', 'Coadministration with lumacaftor may decrease the plasma concentrations and therapeutic efficacy of drugs that are substrates of CYP450 3A4.  Lumacaftor is a potent CYP450 3A4 inducer in vivo.', NULL, 'Concomitant use of lumacaftor is not recommended with sensitive CYP450 3A4 substrates (e.', NULL, 'Concomitant use of lumacaftor is not recommended with sensitive CYP450 3A4 substrates (e.g., oral midazolam, triazolam, lovastatin, simvastatin) or CYP450 3A4 substrates with a narrow therapeutic index (e.g., immunosuppressants such as cyclosporine, everolimus, sirolimus, and tacrolimus).  Lumacaftor should also be avoided in patients receiving certain antibiotics (e.g., clarithromycin, erythromycin, telithromycin) and azole antifungal agents (e.g., itraconazole, ketoconazole, posaconazole, voriconazole).  Alternatives to these agents should be considered.  Some authorities recommend avoiding coadministration of lumacaftor-ivacaftor and itraconazole from 2 weeks before, during and for 2 weeks after treatment with itraconazole.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301162/', '[1] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceuticals  (2002):[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Orkambi (ivacaftor-lumacaftor)." Vertex Pharmaceuticals  (2015):', 1767365211);
